import React, { useEffect, useState, useContext } from "react";
import jsPDF from "jspdf";
import  {AuthContext}   from "../context/AuthContext";
// ...existing code...

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: "",
    crop_pic: ""
  });
  const [picPreview, setPicPreview] = useState("");
  const [requestsMap, setRequestsMap] = useState({}); // cropId -> requests
  const [selectedRequests, setSelectedRequests] = useState({}); // cropId -> dropdown open
  const [dealPopup, setDealPopup] = useState(null); // {request, crop}
    const [accepting, setAccepting] = useState(false);
    const [deleting, setDeleting] = useState({}); // cropId -> bool

  // Fetch farmer's crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
  const myCrops = data.filter(c => c.farmer_id === user.id);
      setCrops(myCrops);
      // Fetch requests for each crop
      for (const crop of myCrops) {
        const reqRes = await fetch(`http://localhost:5000/api/marketplace/${crop.id}/requests`, { credentials: "include" });
        if (reqRes.ok) {
          const reqData = await reqRes.json();
          setRequestsMap(prev => ({ ...prev, [crop.id]: reqData }));
        }
      }
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

    // Delete crop
    const handleDeleteCrop = async (cropId) => {
      setDeleting(d => ({ ...d, [cropId]: true }));
      try {
        await fetch(`http://localhost:5000/api/marketplace/delete/${cropId}`, {
          method: "DELETE",
          credentials: "include"
        });
        fetchCrops();
      } catch (err) {
        alert("Error deleting crop.");
      }
      setDeleting(d => ({ ...d, [cropId]: false }));
    };

    // Accept dealer request
    const handleAcceptRequest = async (crop, request) => {
      setAccepting(true);
      try {
        await fetch(`http://localhost:5000/api/marketplace/accept-request/${crop.id}/${request.id}`, {
          method: "POST",
          credentials: "include"
        });
        fetchCrops();
        setDealPopup({ request, crop });
      } catch (err) {
        alert("Error accepting request.");
      }
      setAccepting(false);
    };

  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(f => ({ ...f, crop_pic: reader.result }));
        setPicPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form)
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "", crop_pic: "" });
      setPicPreview("");
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  const fetchCropsCallback = React.useCallback(fetchCrops, [user]);
  useEffect(() => {
    fetchCropsCallback();
    const interval = setInterval(fetchCropsCallback, 5000);
    return () => clearInterval(interval);
  }, [fetchCropsCallback]);

  return (
    <div style={{
      minHeight: '100vh',
      background: `url('https://cdn.pixabay.com/photo/2020/06/02/05/39/barley-5249498_1280.jpg') center/cover no-repeat, linear-gradient(135deg, #e0f7fa 0%, #f9fbe7 100%)`,
      padding: '32px 0'
    }}>
      <div style={{ maxWidth: 900, margin: '0 auto', padding: '24px 0' }}>
        <h2 style={{ textAlign: 'center', fontSize: 32, fontWeight: 700, marginBottom: 24, color: '#26a69a' }}>🌾 Farmer Marketplace</h2>

        {/* Add Crop Form */}
        <div style={{
          background: '#fff',
          borderRadius: 18,
          boxShadow: '0 4px 18px #b2dfdb',
          padding: 36,
          marginBottom: 40,
          maxWidth: 520,
          marginLeft: 'auto',
          marginRight: 'auto',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}>
          <div style={{ fontSize: 38, color: '#26a69a', marginBottom: 10 }}>🌱</div>
          <h3 style={{ fontWeight: 700, marginBottom: 18, color: '#00897b', fontSize: 24 }}>Add New Crop</h3>
          <form style={{ width: '100%' }} onSubmit={e => { e.preventDefault(); handleAddCrop(); }}>
            <div style={{ marginBottom: 16 }}>
              <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Crop Name</label>
              <input
                placeholder=" name"
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
                style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                required
              />
            </div>
            <div style={{ marginBottom: 16, display: 'flex', gap: 12 }}>
              <div style={{ flex: 1 }}>
                <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Quantity</label>
                <input
                  placeholder=" 100"
                  type="number"
                  value={form.quantity}
                  onChange={e => setForm({ ...form, quantity: e.target.value })}
                  style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                  required
                />
              </div>
              <div style={{ flex: 1 }}>
                <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Unit</label>
                <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })} style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}>
                  <option value="kg">kg</option>
                  <option value="unit">unit</option>
                </select>
              </div>
            </div>
            <div style={{ marginBottom: 16 }}>
              <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Price per unit/kg</label>
              <input
                placeholder="price"
                type="number"
                value={form.base_price}
                onChange={e => setForm({ ...form, base_price: e.target.value })}
                style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                required
              />
            </div>
            <div style={{ marginBottom: 16 }}>
              <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Crop Image</label>
              <input type="file" accept="image/*" onChange={handlePicChange} style={{ width: '100%', marginBottom: 4 }} />
              {picPreview && <img src={picPreview} alt="Preview" style={{maxWidth:120, marginTop:8, borderRadius:10, boxShadow:'0 2px 8px #b2dfdb'}} />}
            </div>
            <button type="submit" style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 0', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', width: '100%', marginTop: 10, boxShadow: '0 2px 8px #b2dfdb' }}>
              <span style={{ fontSize: 20, marginRight: 8 }}>➕</span> Add Crop
            </button>
          </form>
        </div>

        {/* Crops Section */}
        <div style={{ marginTop: 24 }}>
          <h3 style={{ fontWeight: 600, marginBottom: 18, color: '#00897b' }}>Your Crops</h3>
          {crops.length === 0 && <p style={{ textAlign: 'center', color: '#888' }}>No crops added yet.</p>}

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))', gap: 24 }}>
            {crops.map(c => {
              const requests = requestsMap[c.id] || [];
              return (
                <div key={c.id} style={{
                  background: '#fff',
                  borderRadius: 12,
                  boxShadow: '0 2px 8px #b2dfdb',
                  padding: 22,
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'flex-start',
                  minHeight: 220,
                  position: 'relative'
                }}>
                  {/* Top left: number of dealer requests */}
                  <div style={{ position: 'absolute', top: 10, left: 10, background: '#26c6da', color: '#fff', borderRadius: '50%', width: 32, height: 32, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 700, fontSize: 18 }}>
                    {requests.length}
                  </div>
                  {/* Crop image */}
                  {c.crop_pic && <img src={c.crop_pic} alt="Crop" style={{maxWidth:100, marginBottom:8, borderRadius:8}} />}
                  <div style={{ marginBottom: 10 }}>
                    <h3 style={{ fontSize: 22, fontWeight: 700, color: '#26a69a', marginBottom: 6 }}>{c.name}</h3>
                    <p style={{ margin: 0, fontSize: 16 }}><strong>Quantity:</strong> {c.quantity} {c.unit}</p>
                    <p style={{ margin: 0, fontSize: 16 }}><strong>Base Price:</strong> {c.base_price} Tk</p>
                    <p style={{ margin: 0, fontSize: 16 }}><strong>Highest Bid:</strong> {c.highest_bid || "No bids yet"}</p>
                  </div>
                    {/* Delete crop button */}
                    <button
                      style={{ position: 'absolute', top: 10, right: 10, background: '#e57373', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 12px', fontWeight: 600, cursor: 'pointer' }}
                      onClick={() => handleDeleteCrop(c.id)}
                      disabled={deleting[c.id]}
                    >
                      {deleting[c.id] ? 'Deleting...' : 'Delete'}
                    </button>
                  {/* Dealer requests dropdown */}
                  {requests.length > 0 && (
                    <div style={{ margin: '10px 0' }}>
                      <button style={{ background: '#26a69a', color: '#fff', borderRadius: 6, padding: '6px 14px', fontWeight: 600 }} onClick={() => setSelectedRequests(s => ({ ...s, [c.id]: !s[c.id] }))}>
                        Dealer Requests ▼
                      </button>
                      {selectedRequests[c.id] && (
                        <div style={{ background: '#f0f0f0', borderRadius: 8, marginTop: 8, padding: 8 }}>
                          {requests.map(req => (
                            <div key={req.id} style={{ marginBottom: 8, borderBottom: '1px solid #ddd', paddingBottom: 6 }}>
                              <div><strong>Dealer:</strong> {req.dealer_name || req.dealer_id}</div>
                              <div><strong>Price:</strong> {req.bid_price} Tk</div>
                              <div><strong>Quantity:</strong> {req.requested_quantity} {req.unit}</div>
                              <div><strong>Phone:</strong> {req.dealer_phone}</div>
                              <div><strong>Address:</strong> {req.dealer_address}</div>
                                <button
                                  style={{ background: '#43a047', color: '#fff', borderRadius: 6, padding: '4px 10px', marginTop: 6 }}
                                  onClick={() => handleAcceptRequest(c, req)}
                                  disabled={accepting || req.status === 'accepted'}
                                >
                                  {req.status === 'accepted' ? 'Accepted' : 'Accept'}
                                </button>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
      {/* Deal Done Popup */}
      {dealPopup && (
        <div className="popup">
          <h3>🎉 Congratulations! Deal Done</h3>
          <p>Dealer <strong>{dealPopup.request.dealer_name || dealPopup.request.dealer_id}</strong> will buy <strong>{dealPopup.request.requested_quantity} {dealPopup.request.unit}</strong> of <strong>{dealPopup.crop.name}</strong> at <strong>{dealPopup.request.bid_price} Tk</strong>.</p>
          <button onClick={() => {
            // Generate PDF
            const doc = new jsPDF();
            doc.setFontSize(18);
            doc.text('AgroVision Deal Confirmation', 20, 20);
            doc.setFontSize(12);
            doc.text(`Date: ${new Date().toLocaleDateString()}`, 20, 30);
            doc.text(`Farmer: ${user.name} (ID: ${user.id})`, 20, 40);
            doc.text(`Dealer: ${dealPopup.request.dealer_name || dealPopup.request.dealer_id}`, 20, 50);
            doc.text(`Dealer Phone: ${dealPopup.request.dealer_phone}`, 20, 60);
            doc.text(`Dealer Address: ${dealPopup.request.dealer_address}`, 20, 70);
            doc.text(`Crop: ${dealPopup.crop.name}`, 20, 80);
            doc.text(`Quantity: ${dealPopup.request.requested_quantity} ${dealPopup.request.unit}`, 20, 90);
            doc.text(`Price: ${dealPopup.request.bid_price} Tk`, 20, 100);
            doc.text(`Base Price: ${dealPopup.crop.base_price} Tk`, 20, 110);
            doc.save(`deal_request_${dealPopup.request.id}_${Date.now()}.pdf`);
            setDealPopup(null);
          }}>Download PDF</button>
          <button style={{ marginLeft: 10 }} onClick={() => setDealPopup(null)}>Close</button>
        </div>
      )}
        </div>
      </div>
    </div>
  );
}
