import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import AuthProvider from "./context/AuthContext"; // ✅ correct import

import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Home from "./pages/Home";
import Profile from "./pages/Profile";
import ProtectedRoute from "./components/ProtectedRoute";
import FarmerMarketplace from "./pages/FarmerMarketplace";
import DealerMarketplace from "./pages/DealerMarketplace";
import FarmerHomePage from "./pages/FarmerHomePage"; 
import DealerDetailsPage from "./pages/DealerDetailsPage";

function LandingPage() {
  return (
    <div className="landing">
      <header className="landing-hero">
        <h1>Welcome to AgroVision</h1>
        <p>Empowering Farmers & Dealers with Knowledge and Marketplace</p>
        <a href="/register" className="cta-btn">Get Started</a>
      </header>
      {/* Other sections... */}
    </div>
  );
}

function App() {
  return (
    <AuthProvider> {/* ✅ Wrap entire app with provider */}
      <BrowserRouter>
        <Navbar />
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dealer/:dealerId/request/:requestId" element={<DealerDetailsPage />} />

          <Route path="/home" element={<ProtectedRoute><Home /></ProtectedRoute>} />
          <Route path="/profile" element={<ProtectedRoute><Profile /></ProtectedRoute>} />
          <Route path="/farmer-home" element={<ProtectedRoute><FarmerHomePage /></ProtectedRoute>} />
          <Route path="/farmer-marketplace" element={<ProtectedRoute><FarmerMarketplace /></ProtectedRoute>} />
          <Route path="/dealer-marketplace" element={<ProtectedRoute><DealerMarketplace /></ProtectedRoute>} />
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  );
}

export default App;
