import React, { useEffect, useState, useContext } from "react";
import jsPDF from "jspdf";
import  {AuthContext}   from "../context/AuthContext";
import { io } from "socket.io-client";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: "",
    crop_pic: "",
    discount: "",
    minQuantity: ""
  });
  const [picPreview, setPicPreview] = useState("");
  const [showAddModal, setShowAddModal] = useState(false);
  const [showOfferModal, setShowOfferModal] = useState(false);
  const [offerForm, setOfferForm] = useState({ price: "", quantity: "", discount: "", minQuantity: "" });
  const [selectedCrop, setSelectedCrop] = useState(null);

  // Fetch all marketplace crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  useEffect(() => {
    fetchCrops();
    // Inline fetchNotifications to avoid missing dependency warning
    (async () => {
      if (!user) return;
      try {
        const res = await fetch(
          `http://localhost:5000/api/requests/notifications/${user.id}`,
          { credentials: "include" }
        );
        if (res.ok) {
          const data = await res.json();
          setNotifications(data.notifications || []);
        }
      } catch (err) {
        console.error("Error fetching notifications:", err);
      }
    })();
    // Socket.IO for real-time notifications
    if (!user) return;
    const socket = io("http://localhost:5000", { withCredentials: true });
    socket.emit("joinRoom", `farmer_${user.id}`);
    socket.on("newRequest", (notif) => {
      setNotifications((prev) => [notif, ...prev]);
    });
    return () => socket.disconnect();
  }, [user]);

  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(f => ({ ...f, crop_pic: reader.result }));
        setPicPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add-direct", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          name: form.name,
          quantity: form.quantity,
          unit: form.unit,
          price: form.base_price,
          crop_pic: form.crop_pic,
          discount: form.discount,
          minQuantity: form.minQuantity
        })
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "", crop_pic: "", discount: "", minQuantity: "" });
      setPicPreview("");
      setShowAddModal(false);
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  // Handle offer modal open
  const openOfferModal = (crop) => {
    setSelectedCrop(crop);
    setOfferForm({ price: crop.base_price, quantity: "" });
    setShowOfferModal(true);
  };

  // Handle offer submit
  const handleOfferSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch(`http://localhost:5000/api/marketplace/request/${selectedCrop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          requested_quantity: offerForm.quantity,
          bid_price: offerForm.price,
          dealer_phone: user.phone || "",
          dealer_address: user.address || "",
          unit: selectedCrop.unit
        })
      });
      setShowOfferModal(false);
      setOfferForm({ price: "", quantity: "" });
      setSelectedCrop(null);
      fetchCrops();
      alert("Offer submitted!");
    } catch (err) {
      alert("Error submitting offer.");
    }
  };

  // Mark notification as read
  const handleClickNotification = async (notif) => {
    if (!notif.read_flag) {
      await fetch(
        `http://localhost:5000/api/requests/notifications/read/${notif.id}`,
        { method: "POST", credentials: "include" }
      );
      setNotifications((prev) =>
        prev.map((n) => (n.id === notif.id ? { ...n, read_flag: 1 } : n))
      );
    }
    // Optionally navigate to details page
    // window.location.href = `/dealer/${notif.dealer_id}/request/${notif.request_id}`;
  };

  const unreadCount = notifications.filter((n) => !n.read_flag).length;

  return (
    <div style={{ minHeight: '100vh', background: `url('https://cdn.pixabay.com/photo/2020/06/02/05/39/barley-5249498_1280.jpg') center/cover no-repeat, linear-gradient(135deg, #e0f7fa 0%, #f9fbe7 100%)`, padding: '32px 0' }}>
      <div style={{ maxWidth: 1200, margin: '0 auto', padding: '24px 0' }}>
        {/* Navbar with Add Crop button ONLY */}
        <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32 }}>
          <h2 style={{ fontSize: 32, fontWeight: 700, color: '#26a69a' }}>ðŸŒ¾ Farmer Marketplace</h2>
          <button style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '10px 24px', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', boxShadow: '0 2px 8px #b2dfdb' }} onClick={() => setShowAddModal(true)}>
            <span style={{ fontSize: 20, marginRight: 8 }}>âž•</span> Add Crop
          </button>
        </nav>
        {/* Add Crop Modal */}
        {showAddModal && (
          <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
            <div style={{ background: '#fff', borderRadius: 18, boxShadow: '0 4px 18px #b2dfdb', padding: 36, maxWidth: 420, width: '100%', position: 'relative' }}>
              <button style={{ position: 'absolute', top: 12, right: 12, background: '#e57373', color: '#fff', border: 'none', borderRadius: 6, padding: '4px 10px', fontWeight: 600, cursor: 'pointer' }} onClick={() => setShowAddModal(false)}>X</button>
              <h3 style={{ fontWeight: 700, marginBottom: 18, color: '#00897b', fontSize: 24 }}>Add New Crop</h3>
              <form style={{ width: '100%' }} onSubmit={e => { e.preventDefault(); handleAddCrop(); }}>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Crop Name</label>
                  <input
                    placeholder=" name"
                    value={form.name}
                    onChange={e => setForm({ ...form, name: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    required
                  />
                </div>
                <div style={{ marginBottom: 16, display: 'flex', gap: 12 }}>
                  <div style={{ flex: 1 }}>
                    <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Quantity</label>
                    <input
                      placeholder=" 100"
                      type="number"
                      value={form.quantity}
                      onChange={e => setForm({ ...form, quantity: e.target.value })}
                      style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                      required
                    />
                  </div>
                  <div style={{ flex: 1 }}>
                    <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Unit</label>
                    <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })} style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}>
                      <option value="kg">kg</option>
                      <option value="unit">unit</option>
                    </select>
                  </div>
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Price per unit/kg</label>
                  <input
                    placeholder="price"
                    type="number"
                    value={form.base_price}
                    onChange={e => setForm({ ...form, base_price: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    required
                  />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Crop Image</label>
                  <input type="file" accept="image/*" onChange={handlePicChange} style={{ width: '100%', marginBottom: 4 }} />
                  {picPreview && <img src={picPreview} alt="Preview" style={{maxWidth:120, marginTop:8, borderRadius:10, boxShadow:'0 2px 8px #b2dfdb'}} />}
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Discount (%)</label>
                  <input
                    type="number"
                    value={form.discount}
                    onChange={e => setForm({ ...form, discount: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    min="0"
                    max="100"
                  />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Min Quantity for Discount</label>
                  <input
                    type="number"
                    value={form.minQuantity}
                    onChange={e => setForm({ ...form, minQuantity: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    min="1"
                  />
                </div>
                <button type="submit" style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 0', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', width: '100%', marginTop: 10, boxShadow: '0 2px 8px #b2dfdb' }}>
                  <span style={{ fontSize: 20, marginRight: 8 }}>âž•</span> Add Crop
                </button>
              </form>
            </div>
          </div>
        )}
        {/* Offer Modal */}
        {showOfferModal && selectedCrop && (
          <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
            <div style={{ background: '#fff', borderRadius: 18, boxShadow: '0 4px 18px #b2dfdb', padding: 36, maxWidth: 420, width: '100%', position: 'relative' }}>
              <button style={{ position: 'absolute', top: 12, right: 12, background: '#e57373', color: '#fff', border: 'none', borderRadius: 6, padding: '4px 10px', fontWeight: 600, cursor: 'pointer' }} onClick={() => setShowOfferModal(false)}>X</button>
              <h3 style={{ fontWeight: 700, marginBottom: 18, color: '#00897b', fontSize: 24 }}>Add Offer for {selectedCrop.name}</h3>
              <form style={{ width: '100%' }} onSubmit={handleOfferSubmit}>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Offer Price</label>
                  <input
                    type="number"
                    value={offerForm.price}
                    onChange={e => setOfferForm({ ...offerForm, price: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    required
                  />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Quantity</label>
                  <input
                    type="number"
                    value={offerForm.quantity}
                    onChange={e => setOfferForm({ ...offerForm, quantity: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    required
                  />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Discount (%)</label>
                  <input
                    type="number"
                    value={offerForm.discount}
                    onChange={e => setOfferForm({ ...offerForm, discount: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    min="0"
                    max="100"
                  />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label style={{ fontWeight: 500, color: '#00897b', marginBottom: 6, display: 'block' }}>Min Quantity for Discount</label>
                  <input
                    type="number"
                    value={offerForm.minQuantity}
                    onChange={e => setOfferForm({ ...offerForm, minQuantity: e.target.value })}
                    style={{ width: '100%', padding: '12px 16px', borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17, marginBottom: 4 }}
                    min="1"
                  />
                </div>
                <button type="submit" style={{ background: '#43a047', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 0', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', width: '100%', marginTop: 10, boxShadow: '0 2px 8px #b2dfdb' }}>
                  Submit Offer
                </button>
              </form>
            </div>
          </div>
        )}
        {/* Full-page grid of crops */}
        <div style={{ marginTop: 24 }}>
          <h3 style={{ fontWeight: 600, marginBottom: 18, color: '#00897b' }}>All Marketplace Crops</h3>
          {crops.length === 0 && <p style={{ textAlign: 'center', color: '#888' }}>No crops available.</p>}
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: 32 }}>
            {crops.map(crop => (
              <div key={crop.id} style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 18px #b2dfdb', padding: 28, display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: 340, position: 'relative' }}>
                {crop.crop_pic && <img src={crop.crop_pic} alt={crop.name} style={{ width: 180, height: 140, objectFit: 'cover', borderRadius: 12, marginBottom: 18, boxShadow: '0 2px 8px #b2dfdb' }} />}
                <h3 style={{ fontSize: 24, fontWeight: 700, color: '#26a69a', marginBottom: 10 }}>{crop.name} {crop.discount && crop.minQuantity ? <span title="Special Offer" style={{marginLeft:8}}>ðŸŽ</span> : null}</h3>
                <p style={{ fontSize: 18, margin: 0 }}><strong>Price:</strong> {crop.price || crop.base_price} Tk</p>
                <p style={{ fontSize: 18, margin: 0 }}><strong>Quantity:</strong> {crop.quantity} {crop.unit}</p>
                {/* Discount badge if offer exists */}
                {crop.discount && crop.minQuantity && (
                  <div style={{ background: '#ffb300', color: '#fff', borderRadius: 8, padding: '6px 16px', fontWeight: 700, fontSize: 16, margin: '12px 0' }}>
                    {crop.discount}% discount for min {crop.minQuantity} quantity
                  </div>
                )}
                <button style={{ background: '#43a047', color: '#fff', borderRadius: 10, padding: '10px 24px', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', marginTop: 18, boxShadow: '0 2px 8px #b2dfdb' }} onClick={() => openOfferModal(crop)}>
                  Add Offer
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
