import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function DealerDetailsPage() {
  const { dealerId, requestId } = useParams();
  const [details, setDetails] = useState(null);
  const [loading, setLoading] = useState(true); // loading state
  const [error, setError] = useState(null);
  const [actionStatus, setActionStatus] = useState(null);
  const [pdfUrl, setPdfUrl] = useState(null);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        setLoading(true);
        const res = await fetch(
          `http://localhost:5000/api/requests/${requestId}`,
          { credentials: "include" }
        );
        if (!res.ok) throw new Error("Failed to fetch dealer details");
        const data = await res.json();
        setDetails(data);
      } catch (err) {
        console.error(err);
        setError(err.message || "Something went wrong");
      } finally {
        setLoading(false);
      }
    };
    fetchDetails();
  }, [dealerId, requestId]);

  // Accept request
  const handleAccept = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/accept/${requestId}`,
        {
          method: "POST",
          credentials: "include",
        }
      );
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to accept request");
      setActionStatus("accepted");
      setPdfUrl(data.pdfUrl || null); // backend should return PDF URL
    } catch (err) {
      setActionStatus("error");
    }
  };

  // Reject request
  const handleReject = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/reject/${requestId}`,
        {
          method: "POST",
          credentials: "include",
        }
      );
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to reject request");
      setActionStatus("rejected");
    } catch (err) {
      setActionStatus("error");
    }
  };

  if (loading) return <p>Loading dealer request details...</p>;
  if (error) return <p className="text-red-500">{error}</p>;
  if (!details) return <p>No details found.</p>;

  const totalPrice = details.bid_price * details.requested_quantity;

  return (
    <div className="p-4 bg-white shadow rounded">
      <h2 className="text-xl font-bold mb-2">Dealer Request Details</h2>

      <div className="mb-4">
        <p><strong>Dealer Name:</strong> {details.dealer_name}</p>
        <p><strong>Email:</strong> {details.dealer_email || "N/A"}</p>
        <p><strong>Phone:</strong> {details.dealer_phone || "N/A"}</p>
        <p><strong>Address:</strong> {details.dealer_address || "N/A"}</p>
      </div>

      <h3 className="text-lg font-semibold mb-2">Crop Info</h3>
      <div>
        <p><strong>Crop:</strong> {details.crop_name}</p>
        <p><strong>Quantity:</strong> {details.requested_quantity} {details.unit}</p>
        <p><strong>Bid Price:</strong> {details.bid_price} Tk</p>
        <p><strong>Total Price:</strong> {totalPrice} Tk</p>
        <p><strong>Status:</strong> {details.status || "Pending"}</p>
      </div>
      {/* Accept/Reject buttons if pending */}
      {details.status === "pending" && (
        <div className="mt-4 flex gap-4">
          <button onClick={handleAccept} className="px-4 py-2 bg-green-600 text-white rounded">Accept</button>
          <button onClick={handleReject} className="px-4 py-2 bg-red-600 text-white rounded">Reject</button>
        </div>
      )}
      {/* Show PDF download if accepted */}
      {actionStatus === "accepted" && pdfUrl && (
        <div className="mt-4">
          <a href={pdfUrl} download className="px-4 py-2 bg-blue-600 text-white rounded">Download PDF</a>
        </div>
      )}
      {actionStatus === "rejected" && (
        <p className="mt-4 text-red-500">Request rejected.</p>
      )}
      {actionStatus === "error" && (
        <p className="mt-4 text-red-500">Action failed. Please try again.</p>
      )}
    </div>
  );
}
