import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { io } from "socket.io-client";

// Helper function to show "time ago"
function timeAgo(dateString) {
  const now = new Date();
  const past = new Date(dateString);
  const diff = Math.floor((now - past) / 1000); // in seconds

  if (diff < 60) return `${diff} sec${diff !== 1 ? 's' : ''} ago`;
  const minutes = Math.floor(diff / 60);
  if (minutes < 60) return `${minutes} min${minutes !== 1 ? 's' : ''} ago`;
  const hours = Math.floor(diff / 3600);
  if (hours < 24) return `${hours} hr${hours !== 1 ? 's' : ''} ago`;
  const days = Math.floor(hours / 24);
  return `${days} day${days !== 1 ? 's' : ''} ago`;
}

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);
  const [socket, setSocket] = useState(null);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch notifications
  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/notifications/${user.id}`,
        { credentials: "include" }
      );
      if (res.ok) {
        const data = await res.json();
        setNotifications(data.notifications || []);
      }
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  };

  useEffect(() => {
    if (!user) return;

    fetchNotifications();

    // Connect Socket.IO
    const newSocket = io("http://localhost:5000", { withCredentials: true });
    setSocket(newSocket);

    // Join farmer room
    newSocket.emit("joinRoom", `farmer_${user.id}`);

    // Listen for new notifications
    newSocket.on("newRequest", (notif) => {
      setNotifications((prev) => [notif, ...prev]);
    });

    return () => newSocket.disconnect();
  }, [user]);

  const handleClickNotification = async (n) => {
    nav(`/dealer/${n.dealer_id}/request/${n.request_id}`);

    if (!n.read_flag) {
      try {
        await fetch(`http://localhost:5000/api/requests/notifications/read/${n.id}`, {
          method: "POST",
          credentials: "include",
        });
        setNotifications((prev) =>
          prev.map((item) =>
            item.id === n.id ? { ...item, read_flag: 1 } : item
          )
        );
      } catch (err) {
        console.error("Failed to mark notification as read:", err);
      }
    }

    setOpen(false);
  };

  const unreadCount = notifications.filter((n) => !n.read_flag).length;

  return (
    <nav className="navbar flex justify-between items-center p-4 bg-white shadow">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>

      <div className="nav-right flex items-center gap-4">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* Notification Bell */}
            <div className="notification-wrapper relative">
              <button
                className="notification-bell relative text-2xl"
                onClick={() => setOpen(!open)}
              >
                üîî
                {unreadCount > 0 && (
                  <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full animate-pulse"></span>
                )}
              </button>

              {open && (
                <div className="notification-dropdown absolute right-0 mt-2 w-80 bg-white shadow-lg rounded z-50 max-h-96 overflow-y-auto">
                  <h4 className="p-2 font-bold border-b">Notifications</h4>
                  {notifications.length === 0 ? (
                    <p className="p-3 text-sm text-gray-500">No notifications</p>
                  ) : (
                    notifications.map((n) => (
                      <div
                        key={n.id}
                        onClick={() => handleClickNotification(n)}
                        className={`p-3 border-b cursor-pointer rounded ${
                          n.read_flag
                            ? "bg-white text-black"
                            : "bg-blue-50 text-blue-800 font-medium"
                        }`}
                      >
                        <span className="block">
                          You have a new request for{" "}
                          <strong>{n.crop_name}</strong> {n.requested_quantity}{" "}
                          {n.unit} at {n.bid_price} Tk time: 
                        </span>
                        <span className="text-xs text-gray-500 flex justify-between items-center">
                          {timeAgo(n.created_at)}
                          {!n.read_flag && (
                            <span className="ml-2 text-red-500">‚óè</span>
                          )}
                        </span>
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar w-8 h-8 rounded-full"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
