import { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {AuthContext}   from '../context/AuthContext';


export default function Home() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (user && user.role === "farmer") {
      navigate("/farmer-home");
    }
  }, [user, navigate]);

  const handleMarketplaceClick = () => {
    if (!user) {
      alert("Please log in first!");
      return;
    }
    if (user.role === "farmer") {
      navigate("/farmer-marketplace");
    } else if (user.role === "dealer") {
      navigate("/dealer-marketplace");
    } else {
      alert("Invalid role");
    }
  };


  return (
    <div>
      {/* Hero Section */}
      <header className="hero">
        <div className="hero-overlay">
          <h1>
            Welcome to <span className="highlight">AgroVision</span>
          </h1>
          <p>Empowering farmers & dealers with technology.</p>
        </div>
      </header>

      {/* Features Section */}
      <main className="container">
        <h2 className="section-title">Our Services</h2>
        <section className="cards">
          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Marketplace"
              alt="Marketplace"
              className="card-img"
            />
            <h3>Marketplace</h3>
            <p>
              Farmers can sell crops, dealers can bid & buy at fair prices.
            </p>
            <button onClick={handleMarketplaceClick}>Go to Marketplace</button>
          </div>

          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Knowledge+Center"
              alt="Knowledge"
              className="card-img"
            />
            <h3>Knowledge</h3>
            <p>Access crop guides, tips, and weather forecasts.</p>
            <button onClick={() => navigate("/knowledge")}>
              Open Guides
            </button>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="site-footer">
        <div>
          © {new Date().getFullYear()} AgroVision — Contact: +880 xxxxxxxx
        </div>
      </footer>
    </div>
  );
}
