import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch dealer requests for farmer
  const fetchNotifications = async () => {
    if (!user) return;
    try {
      // fetch all dealer requests for this farmer
      const res = await fetch(`http://localhost:5000/api/requests/farmer/${user.id}`, { credentials: "include" });
      const data = await res.json();

      // map requests into notification format
      const formatted = data.map(req => ({
        id: req.id,
        type: "dealer_request",
        title: `New request from ${req.dealer_name}`,
        message: `${req.dealer_name} requested ${req.requested_quantity} ${req.unit} of ${req.crop_name} at ${req.bid_price} Tk`,
        request_id: req.id,
      }));

      setNotifications(formatted);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  // Accept or Reject request
  const handleAction = async (requestId, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/handle/${requestId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ status }),
      });
      if (res.ok) fetchNotifications(); // refresh notifications
    } catch (err) {
      console.error("Error handling request", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // fetch every 5s
    return () => clearInterval(interval);
  }, [user]);

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>
      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* Notification Bell */}
            <div className="notification-wrapper">
              <button className="notification-bell" onClick={() => setOpen(!open)}>
                ðŸ””
                {notifications.length > 0 && <span className="badge">{notifications.length}</span>}
              </button>

              {open && (
                <div className="notification-dropdown">
                  <h4>Notifications</h4>
                  {notifications.length === 0 ? (
                    <p>No new notifications</p>
                  ) : (
                    notifications.map((n) => (
                      <div key={n.id} className="notification-item">
                        <strong>{n.title}</strong>
                        <p>{n.message}</p>

                        {user.role === "farmer" && n.type === "dealer_request" && (
                          <div className="action-buttons">
                            <button className="accept" onClick={() => handleAction(n.request_id, "accepted")}>
                              Accept
                            </button>
                            <button className="reject" onClick={() => handleAction(n.request_id, "rejected")}>
                              Reject
                            </button>
                          </div>
                        )}
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img src={user.profile_pic || "/default-avatar.png"} alt="profile" className="nav-avatar" />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
