// src/pages/NotificationsPage.jsx
import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function NotificationsPage() {
  const { user } = useContext(AuthContext);
  const [notifications, setNotifications] = useState([]);
  const nav = useNavigate();

  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(`http://localhost:5000/api/requests/notifications/${user.id}`, {
        credentials: "include"
      });
      const data = await res.json();
      setNotifications(data);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // refresh every 5s
    return () => clearInterval(interval);
  }, [user]);

  return (
    <div className="notifications-page-container">
      <h2>ðŸ“‹ Notifications</h2>

      {notifications.length === 0 ? (
        <p>No notifications yet.</p>
      ) : (
        <div className="notification-cards">
          {notifications.map((n) => (
            <div key={n.id} className="notification-card">
              <p>
                Dealer:{" "}
                <span
                  className="dealer-link"
                  style={{ color: "#2a7ae2", cursor: "pointer" }}
                  onClick={() => nav(`/dealer/${n.dealer_id}`)}
                >
                  {n.dealer_name}
                </span>
              </p>
              <p>Crop: <strong>{n.crop_name}</strong></p>
              <p>Quantity: {n.requested_quantity} {n.unit}</p>
              <p>Bid Price: {n.bid_price} Tk</p>
              <p>Status: <strong>{n.status || "Pending"}</strong></p>
              <p>Date: {new Date(n.created_at).toLocaleString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
