import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Home() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  const goToMarketplace = () => {
    if (!user) return alert("Please login first!");
    if (user.role === "farmer") navigate("/marketplace/farmer");
    else if (user.role === "dealer") navigate("/marketplace/dealer");
  };

  return (
    <div>
      <header className="hero">
        <div className="hero-overlay">
          <h1>Welcome to <span className="highlight">AgroVision</span></h1>
          <p>Empowering farmers & dealers with technology.</p>
        </div>
      </header>

      <main className="container">
        <h2 className="section-title">Our Services</h2>
        <section className="cards">
          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Marketplace"
              alt="Marketplace"
              className="card-img"
            />
            <h3>Marketplace</h3>
            <p>Buy & sell produce, seeds and equipment with ease.</p>
            <button onClick={goToMarketplace}>Go to Marketplace</button>
          </div>
          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Knowledge+Center"
              alt="Knowledge"
              className="card-img"
            />
            <h3>Knowledge</h3>
            <p>Access crop guides, tips, and weather forecasts.</p>
            <button onClick={() => alert("Knowledge section")}>
              Open Guides
            </button>
          </div>
        </section>
      </main>
    </div>
  );
}
