// frontend/src/pages/FarmerMarketplace.jsx
import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const units = ["kg", "unit", "bundle", "liter"]; // example units

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [myCrops, setMyCrops] = useState([]);
  const [marketplaceForm, setMarketplaceForm] = useState({});
  const [directCrop, setDirectCrop] = useState({});
  const [otherCrops, setOtherCrops] = useState([]);

  const fetchMyMarketplaceCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace/my-crops", { credentials: "include" });
      const data = await res.json();
      const mapped = data.map(c => ({
        ...c,
        name: c.crop_name,
        minQuantity: c.min_quantity
      }));
      setMyCrops(mapped);
    } catch (err) {
      console.error("Error fetching marketplace crops", err);
    }
  };

  const fetchOtherCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace/crops", { credentials: "include" });
      const data = await res.json();
      setOtherCrops(data.filter(c => c.farmer_id !== user.id));
    } catch (err) {
      console.error("Error fetching other crops", err);
    }
  };

  useEffect(() => {
    if (user) fetchMyMarketplaceCrops();
    fetchOtherCrops();
  }, [user]);

  const handleAddDirect = async () => {
    if (!directCrop.name || !directCrop.quantity || !directCrop.unit || !directCrop.price)
      return alert("Fill all required fields (name, quantity, unit, price)");

    try {
      const res = await fetch("http://localhost:5000/api/marketplace/add-direct", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          name: directCrop.name,
          quantity: parseFloat(directCrop.quantity),
          unit: directCrop.unit,
          price: parseFloat(directCrop.price),
          crop_pic: directCrop.crop_pic || "",
          discount: directCrop.discount ? parseFloat(directCrop.discount) : null,
          minQuantity: directCrop.minQuantity ? parseFloat(directCrop.minQuantity) : null
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);
      alert("Crop added directly to marketplace!");
      setDirectCrop({ name: "", quantity: "", unit: "", price: "", crop_pic: "", discount: "", minQuantity: "" });
      fetchMyMarketplaceCrops();
    } catch (err) {
      console.error(err);
      alert("Error adding crop directly");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-6">
      <div className="max-w-7xl mx-auto">

        {/* My Crops */}
        <h2 className="text-3xl font-bold text-teal-700 mb-6">My Marketplace Crops</h2>
        {myCrops.length === 0 ? (
          <p className="text-gray-600 mb-6">No crops in marketplace yet.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-12">
            {myCrops.map(crop => (
              <div key={crop.id} className="bg-white shadow-lg rounded-xl p-6 hover:shadow-xl transition-shadow">
                <h3 className="text-xl font-semibold text-teal-800 mb-2">{crop.name}</h3>
                <p className="text-gray-700">Quantity: {crop.quantity} {crop.unit}</p>
                <p className="text-gray-700">Price: {crop.price} Tk</p>
                {crop.discount && crop.minQuantity && (
                  <p className="text-orange-600 font-semibold">
                    {crop.discount}% discount if buy {crop.minQuantity} {crop.unit} or more
                  </p>
                )}
                <button
                  onClick={async () => {
                    if (!window.confirm("Are you sure you want to remove this crop from the marketplace?")) return;
                    try {
                      const res = await fetch(`http://localhost:5000/api/marketplace/remove/${crop.id}`, {
                        method: 'DELETE',
                        credentials: 'include',
                      });
                      if (res.ok) {
                        alert("Crop removed from marketplace!");
                        fetchMyMarketplaceCrops();
                      } else {
                        const data = await res.json();
                        alert("Error: " + data.message);
                      }
                    } catch (err) {
                      console.error(err);
                      alert("Error removing crop from marketplace");
                    }
                  }}
                  className="mt-4 w-full bg-red-500 hover:bg-red-600 text-white font-semibold py-2 rounded-lg transition-colors"
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Direct Add */}
        <h2 className="text-3xl font-bold text-teal-700 mb-4">Add Existing Crop/Food Directly</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-12">
          <div className="bg-white shadow-lg rounded-xl p-6">
            <input
              type="text"
              placeholder="Crop/Food Name"
              value={directCrop.name || ""}
              onChange={e => setDirectCrop({ ...directCrop, name: e.target.value })}
              className="w-full mb-3 p-3 rounded-lg border border-gray-300"
            />
            <input
              type="number"
              placeholder="Quantity"
              value={directCrop.quantity || ""}
              onChange={e => setDirectCrop({ ...directCrop, quantity: e.target.value })}
              className="w-1/2 mr-2 mb-3 p-3 rounded-lg border border-gray-300"
            />
            <select
              value={directCrop.unit || ""}
              onChange={e => setDirectCrop({ ...directCrop, unit: e.target.value })}
              className="w-1/2 mb-3 p-3 rounded-lg border border-gray-300"
            >
              <option value="">Select Unit</option>
              {units.map(u => <option key={u} value={u}>{u}</option>)}
            </select>
            <input
              type="number"
              placeholder="Price"
              value={directCrop.price || ""}
              onChange={e => setDirectCrop({ ...directCrop, price: e.target.value })}
              className="w-full mb-3 p-3 rounded-lg border border-gray-300"
            />
            <button
              onClick={handleAddDirect}
              className="w-full bg-teal-500 hover:bg-teal-600 text-white font-semibold py-2 rounded-lg transition-colors"
            >
              Add Directly
            </button>
          </div>
        </div>

        {/* Other Farmers’ Crops */}
        <h2 className="text-3xl font-bold text-teal-700 mb-4">Other Farmers’ Crops</h2>
        {otherCrops.length === 0 ? (
          <p className="text-gray-600 mb-6">No other crops available.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {otherCrops.map(crop => (
              <div key={crop.id} className="bg-white shadow-lg rounded-xl p-6 hover:shadow-xl transition-shadow">
                <h3 className="text-xl font-semibold text-teal-800 mb-2">{crop.crop_name}</h3>
                <p className="text-gray-700">Quantity: {crop.quantity} {crop.unit || ""}</p>
                <p className="text-gray-700">Price: {crop.price} Tk</p>
                {crop.highest_bid && <p className="text-orange-600 font-semibold">Highest Bid: {crop.highest_bid} Tk</p>}
                <p className="text-gray-600">Farmer: {crop.farmer_name}</p>
              </div>
            ))}
          </div>
        )}

      </div>
    </div>
  );
}
