import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function DealerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [bidValues, setBidValues] = useState({});
  const [successMsg, setSuccessMsg] = useState(""); // ✅ success message

  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  const handleBid = async (cropId, bidPrice, quantity) => {
    if (!bidPrice || !quantity) return alert("Enter quantity and bid");
    try {
      const res = await fetch(`http://localhost:5000/api/requests/create`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          crop_id: cropId,
          bid_price: parseFloat(bidPrice),
          requested_quantity: parseInt(quantity)
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);

      // ✅ Show popup success message
      setSuccessMsg("Request sent successfully!");
      setTimeout(() => setSuccessMsg(""), 3000); // hide after 3s

      fetchCrops();
      setBidValues({ ...bidValues, [cropId]: "", [`${cropId}_qty`]: "" });
    } catch (err) {
      console.error(err);
      alert("Error sending request");
    }
  };

  useEffect(() => {
    fetchCrops();
    const interval = setInterval(fetchCrops, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dealer-marketplace-container">
      <h2>Dealer Marketplace</h2>

      {successMsg && (
        <div className="popup-success">
          {successMsg}
        </div>
      )}

      {crops.length === 0 && <p>No crops available yet.</p>}

      <div className="crop-cards">
        {crops.map(c => (
          <div key={c.id} className="crop-card">
            <div className="crop-info">
              <h3>{c.name}</h3>
              <p>Farmer: {c.farmer_name || "Unknown"}</p>
              <p>Quantity: {c.quantity} {c.unit}</p>
              <p>Base Price: {c.base_price} Tk</p>
              <p>Highest Bid: {c.highest_bid || "No bids yet"}</p>
            </div>

            <div className="bid-section">
              <input
                type="number"
                placeholder="Quantity"
                value={bidValues[`${c.id}_qty`] || ""}
                onChange={e => setBidValues({ ...bidValues, [`${c.id}_qty`]: e.target.value })}
              />
              <input
                type="number"
                 placeholder={`Your bid (${c.base_price - 5} - ${c.base_price + 5})`}
                value={bidValues[c.id] || ""}
                onChange={e => setBidValues({ ...bidValues, [c.id]: e.target.value })}
              />
              <button onClick={() => handleBid(c.id, bidValues[c.id], bidValues[`${c.id}_qty`])}>
                Send Request
              </button>
            </div>
          </div>
        ))}
      </div>

      
    </div>
  );
}
