import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: ""
  });

  const fetchCrops = async () => {
    const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
    const data = await res.json();

    const myCrops = data.filter(c => c.farmer_id === user.id);
    setCrops(myCrops);
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form)
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "" });
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="farmer-marketplace-container">
      <h2>🌾 Farmer Marketplace</h2>

      {/* Add Crop Form */}
      <div className="add-crop-form">
        <input
          placeholder="Crop Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          placeholder="Quantity"
          type="number"
          value={form.quantity}
          onChange={e => setForm({ ...form, quantity: e.target.value })}
        />
        <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })}>
          <option value="kg">kg</option>
          <option value="unit">unit</option>
        </select>
        <input
          placeholder="Price per unit/kg"
          type="number"
          value={form.base_price}
          onChange={e => setForm({ ...form, base_price: e.target.value })}
        />
        <button onClick={handleAddCrop}>+ Add Crop</button>
      </div>

      <h3>Your Crops</h3>
      {crops.length === 0 && <p>No crops added yet.</p>}

      <div className="crop-cards">
        {crops.map(c => (
          <div key={c.id} className="crop-card">
            <div className="crop-info">
              <h3>{c.name}</h3>
              <p>Quantity: {c.quantity} {c.unit}</p>
              <p>Base Price: {c.base_price} Tk</p>
              <p>Highest Bid: {c.highest_bid || "No bids yet"}</p>
            </div>

            {c.bids && c.bids.length > 0 && (
              <div className="bids-list">
                <h4>Dealer Bids</h4>
                <ul>
                  {c.bids.map(b => (
                    <li key={b.id}>
                      <span>{b.dealer_name}</span>
                      <strong>{b.bid_price} Tk</strong>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Styles */}
      <style jsx>{`
        .farmer-marketplace-container {
          padding: 20px;
          max-width: 1200px;
          margin: auto;
          font-family: Arial, sans-serif;
        }

        h2 {
          text-align: center;
          margin-bottom: 20px;
          color: #2c3e50;
        }

        .add-crop-form {
          display: flex;
          gap: 10px;
          flex-wrap: wrap;
          margin-bottom: 30px;
          background: #f9fafc;
          padding: 15px;
          border-radius: 10px;
          box-shadow: 0 3px 6px rgba(0,0,0,0.08);
        }

        .add-crop-form input,
        .add-crop-form select {
          padding: 10px;
          border: 1px solid #ddd;
          border-radius: 6px;
          flex: 1;
          min-width: 150px;
        }

        .add-crop-form button {
          background: #28a745;
          color: white;
          border: none;
          padding: 10px 15px;
          border-radius: 6px;
          cursor: pointer;
          transition: background 0.2s ease;
        }

        .add-crop-form button:hover {
          background: #218838;
        }

        .crop-cards {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
          gap: 20px;
        }

        .crop-card {
          background: #fff;
          border: 1px solid #eaeaea;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.05);
          padding: 15px;
          transition: transform 0.2s ease, box-shadow 0.2s ease;
        }

        .crop-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 6px 16px rgba(0,0,0,0.1);
        }

        .crop-info h3 {
          margin: 0 0 8px;
          color: #007bff;
        }

        .crop-info p {
          margin: 5px 0;
          color: #444;
          font-size: 0.95rem;
        }

        .bids-list {
          margin-top: 15px;
          padding-top: 10px;
          border-top: 1px solid #eee;
        }

        .bids-list h4 {
          margin-bottom: 8px;
          font-size: 1rem;
          color: #333;
        }

        .bids-list ul {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .bids-list li {
          display: flex;
          justify-content: space-between;
          padding: 5px 0;
          font-size: 0.9rem;
          border-bottom: 1px dashed #eee;
        }

        .bids-list li:last-child {
          border-bottom: none;
        }

        .bids-list strong {
          color: #28a745;
        }
      `}</style>
    </div>
  );
}
