import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch notifications
  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/notifications/${user.id}`,
        { credentials: "include" }
      );
      const data = await res.json();
      setNotifications(data);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // refresh every 5s
    return () => clearInterval(interval);
  }, [user]);

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/">
          <strong>AgroVision</strong>
        </Link>
      </div>

      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* üîî Notification Bell */}
            <div className="notification-wrapper relative">
              <button
                className="notification-bell relative"
                onClick={() => setOpen(!open)}
              >
                üîî
                {/* ‚úÖ show only unread count */}
                {notifications.filter((n) => !n.read_flag).length > 0 && (
                  <span className="badge absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold px-2 rounded-full">
                    {notifications.filter((n) => !n.read_flag).length}
                  </span>
                )}
              </button>

              {open && (
                <div className="notification-dropdown absolute right-0 mt-2 w-80 bg-white shadow-lg rounded z-50">
                  <h4 className="p-2 font-bold border-b">Notifications</h4>
                  {notifications.length === 0 ? (
                    <p className="p-3 text-sm">No notifications</p>
                  ) : (
                    notifications.map((n) => {
                      const createdAt = new Date(n.created_at);
                      const now = new Date();
                      const diffMs = now - createdAt;
                      const diffMinutes = Math.floor(diffMs / 60000);
                      const timeAgo =
                        diffMinutes < 1
                          ? "just now"
                          : diffMinutes < 60
                          ? `${diffMinutes} minutes ago`
                          : diffMinutes < 1440
                          ? `${Math.floor(diffMinutes / 60)} hours ago`
                          : `${Math.floor(diffMinutes / 1440)} days ago`;

                      const handleClick = async () => {
                        nav(`/dealer/${n.dealer_id}/request/${n.request_id}`);
                        if (!n.read_flag) {
                          try {
                            await fetch(
                              `http://localhost:5000/api/notifications/read/${n.id}`,
                              {
                                method: "POST",
                                credentials: "include",
                              }
                            );
                            // ‚úÖ update state so badge decreases instantly
                            setNotifications((prev) =>
                              prev.map((item) =>
                                item.id === n.id
                                  ? { ...item, read_flag: 1 }
                                  : item
                              )
                            );
                          } catch (err) {
                            console.error(
                              "Failed to mark notification as read",
                              err
                            );
                          }
                        }
                        setOpen(false); // auto close dropdown after click
                      };

                      return (
                        <div
                          key={n.id}
                          onClick={handleClick}
                          className={`p-3 border-b cursor-pointer ${
                            n.read_flag
                              ? "bg-gray-100 text-black"
                              : "bg-blue-100 font-bold"
                          }`}
                        >
                          <span className="block">
                            You have a new request for{" "}
                            <strong>{n.crop_name}</strong>{" "}
                            {n.requested_quantity} {n.unit} at {n.bid_price} Tk
                          </span>
                          <span className="text-xs text-gray-600">
                            {timeAgo}{" "}
                            {!n.read_flag && (
                              <span className="ml-2 text-red-600">‚óè New</span>
                            )}
                          </span>
                        </div>
                      );
                    })
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
