import { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function DealerDetailsPage() {
  const { user } = useContext(AuthContext);
  const { dealerId, cropId } = useParams(); // get dealer and crop from URL
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [dealDone, setDealDone] = useState(false);

  // Fetch specific request details
  const fetchRequest = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/for-farmer/${user.id}`, 
        { credentials: "include" }
      );
      const data = await res.json();
      const req = data.find(
        r => r.dealer_id === parseInt(dealerId) && r.crop_id === parseInt(cropId)
      );
      setRequest(req || null);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching request", err);
    }
  };

  useEffect(() => {
    fetchRequest();
  }, []);

  const handleAction = async (status) => {
    if (!request) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/handle/${request.id}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify({ status }),
        }
      );
      const data = await res.json();
      if (res.ok && status === "accepted") {
        setDealDone(true); // show popup
      }
      fetchRequest(); // refresh request status
    } catch (err) {
      console.error("Error handling request", err);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!request) return <p>No request found for this dealer and crop.</p>;

  return (
    <div className="dealer-details-page">
      <h2>Dealer Details</h2>
      <p><strong>Dealer Name:</strong> {request.dealer_name}</p>
      <p><strong>Dealer Phone:</strong> {request.dealer_phone || "N/A"}</p>
      <p><strong>Dealer Address:</strong> {request.dealer_address || "N/A"}</p>
      <p><strong>Crop Name:</strong> {request.crop_name}</p>
      <p><strong>Quantity Requested:</strong> {request.requested_quantity} {request.unit}</p>
      <p><strong>Bid Price:</strong> {request.bid_price} Tk</p>

      {request.status === "pending" && (
        <div className="action-buttons">
          <button onClick={() => handleAction("accepted")} className="accept">
            Accept
          </button>
          <button onClick={() => handleAction("rejected")} className="reject">
            Reject
          </button>
        </div>
      )}

      {dealDone && (
        <div className="popup">
          <h3>ðŸŽ‰ Congratulations! Deal Done</h3>
          {request.receipt_url && (
            <a
              href={request.receipt_url}
              target="_blank"
              rel="noopener noreferrer"
              className="download-pdf"
            >
              Download PDF Receipt
            </a>
          )}
        </div>
      )}
    </div>
  );
}
