// frontend/src/pages/FarmerMarketplace.jsx
import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [myCrops, setMyCrops] = useState([]);
  const [otherCrops, setOtherCrops] = useState([]);
  const [marketplaceForm, setMarketplaceForm] = useState({}); // key: cropId -> { price, quantity }
  const [directCrop, setDirectCrop] = useState({}); // For direct add form
  const [offerCropId, setOfferCropId] = useState(null); // currently editing offer
  const [offerData, setOfferData] = useState({ discount: "", minQuantity: "" }); // offer input values

  // Fetch all crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/crops", { credentials: "include" });
      const data = await res.json();
      // Separate own crops and other farmers
      setMyCrops(data.filter(c => c.farmer_id === user.id));
      setOtherCrops(data.filter(c => c.farmer_id !== user.id));
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  useEffect(() => {
    if (user) fetchCrops();
  }, [user]);

  // Handle adding crop to marketplace
  const handleAddToMarketplace = async (crop) => {
    const formData = marketplaceForm[crop.id];
    if (!formData?.price || !formData?.quantity) return alert("Enter price and quantity");

    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/harvest/${crop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          price: parseFloat(formData.price),
          quantity: parseInt(formData.quantity)
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);
      alert("Crop added to marketplace!");
      setMarketplaceForm({ ...marketplaceForm, [crop.id]: { price: "", quantity: "" } });
      fetchCrops();
    } catch (err) {
      console.error(err);
      alert("Error adding crop to marketplace");
    }
  };

  // Handle direct add of crop/food to marketplace
  const handleAddDirect = async () => {
    if (!directCrop.name || !directCrop.quantity || !directCrop.unit || !directCrop.price) {
      return alert("Please fill all required fields");
    }
    try {
      const res = await fetch("http://localhost:5000/api/marketplace/add-direct", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(directCrop)
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);
      alert("Crop/food added directly to marketplace!");
      setDirectCrop({});
      fetchCrops();
    } catch (err) {
      console.error(err);
      alert("Error adding crop directly");
    }
  };

  // Handle adding/updating offer on a crop
  const handleSetOffer = async (crop) => {
    if (!offerData.discount || !offerData.minQuantity) return alert("Fill discount and minimum quantity");
    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/offer/${crop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          discount: parseFloat(offerData.discount),
          minQuantity: parseInt(offerData.minQuantity)
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);
      alert("Offer updated!");
      setOfferCropId(null);
      setOfferData({ discount: "", minQuantity: "" });
      fetchCrops();
    } catch (err) {
      console.error(err);
      alert("Error updating offer");
    }
  };

  return (
    <div style={{ minHeight: "100vh", padding: "32px 0", background: "#f9fbe7" }}>
      <div style={{ maxWidth: 1200, margin: "0 auto" }}>
        {/* My Crops Section */}
        <h2 style={{ fontSize: 32, fontWeight: 700, color: "#26a69a", marginBottom: 16 }}>My Crops</h2>
        {myCrops.length === 0 && <p>No crops yet.</p>}
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: 24 }}>
          {myCrops.map(crop => (
            <div key={crop.id} style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 2px 6px #b2dfdb" }}>
              <h3 style={{ fontSize: 22, fontWeight: 700, color: "#00897b" }}>{crop.name}</h3>
              <p>Quantity: {crop.quantity} {crop.unit}</p>
              {crop.completion_percentage !== undefined && <p>Completion: {crop.completion_percentage}%</p>}
              {crop.discount && crop.minQuantity && (
                <p style={{ color: "#e65100", fontWeight: 700 }}>
                  {crop.discount}% discount if buy {crop.minQuantity} {crop.unit} or more
                </p>
              )}

              {/* Add to marketplace */}
              {crop.completion_percentage >= 80 && (
                <>
                  <input
                    type="number"
                    placeholder="Base price"
                    value={marketplaceForm[crop.id]?.price || ""}
                    onChange={e => setMarketplaceForm({ ...marketplaceForm, [crop.id]: { ...marketplaceForm[crop.id], price: e.target.value } })}
                    style={{ width: "48%", marginRight: "4%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
                  />
                  <input
                    type="number"
                    placeholder="Quantity"
                    value={marketplaceForm[crop.id]?.quantity || ""}
                    onChange={e => setMarketplaceForm({ ...marketplaceForm, [crop.id]: { ...marketplaceForm[crop.id], quantity: e.target.value } })}
                    style={{ width: "48%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
                  />
                  <button
                    onClick={() => handleAddToMarketplace(crop)}
                    disabled={crop.completion_percentage < 80}
                    style={{
                      marginTop: 10,
                      padding: "8px 16px",
                      borderRadius: 8,
                      background: crop.completion_percentage >= 80 ? "#26c6da" : "#ccc",
                      color: "#fff",
                      border: "none",
                      fontWeight: 600,
                      cursor: crop.completion_percentage >= 80 ? "pointer" : "not-allowed"
                    }}
                  >
                    Add to Marketplace
                  </button>
                </>
              )}

              {/* Offer Button */}
              <button
                onClick={() => setOfferCropId(crop.id)}
                style={{ marginTop: 10, padding: "6px 12px", borderRadius: 6, background: "#ffb300", color: "#fff", border: "none", fontWeight: 600, cursor: "pointer" }}
              >
                Set Offer / Discount
              </button>

              {/* Offer Form */}
              {offerCropId === crop.id && (
                <div style={{ marginTop: 10, padding: 10, background: "#f1f8e9", borderRadius: 6 }}>
                  <input
                    type="number"
                    placeholder="Discount (%)"
                    value={offerData.discount}
                    onChange={e => setOfferData({ ...offerData, discount: e.target.value })}
                    style={{ width: "48%", marginRight: "4%", padding: 6, borderRadius: 4, border: "1px solid #b2dfdb" }}
                  />
                  <input
                    type="number"
                    placeholder="Min Quantity"
                    value={offerData.minQuantity}
                    onChange={e => setOfferData({ ...offerData, minQuantity: e.target.value })}
                    style={{ width: "48%", padding: 6, borderRadius: 4, border: "1px solid #b2dfdb" }}
                  />
                  <button
                    onClick={() => handleSetOffer(crop)}
                    style={{ marginTop: 8, padding: "6px 12px", borderRadius: 6, background: "#26c6da", color: "#fff", border: "none", fontWeight: 600, cursor: "pointer" }}
                  >
                    Submit Offer
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Direct Add New Crop Section */}
        <h2 style={{ fontSize: 32, fontWeight: 700, color: "#26a69a", margin: "40px 0 16px 0" }}>Add Existing Crop/Food Directly</h2>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: 16 }}>
          <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 2px 6px #b2dfdb" }}>
            <input
              type="text"
              placeholder="Crop/Food Name"
              value={directCrop.name || ""}
              onChange={e => setDirectCrop({ ...directCrop, name: e.target.value })}
              style={{ width: "100%", padding: 8, marginBottom: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={directCrop.quantity || ""}
              onChange={e => setDirectCrop({ ...directCrop, quantity: e.target.value })}
              style={{ width: "48%", marginRight: "4%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
            />
            <input
              type="text"
              placeholder="Unit"
              value={directCrop.unit || ""}
              onChange={e => setDirectCrop({ ...directCrop, unit: e.target.value })}
              style={{ width: "48%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
            />
            <input
              type="number"
              placeholder="Price"
              value={directCrop.price || ""}
              onChange={e => setDirectCrop({ ...directCrop, price: e.target.value })}
              style={{ width: "48%", marginRight: "4%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
            />
            <button
              onClick={handleAddDirect}
              style={{ marginTop: 10, padding: "8px 16px", borderRadius: 8, background: "#26c6da", color: "#fff", border: "none", fontWeight: 600, cursor: "pointer" }}
            >
              Add Directly
            </button>
          </div>
        </div>

        {/* Other Farmers’ Crops Section */}
        <h2 style={{ fontSize: 32, fontWeight: 700, color: "#26a69a", margin: "40px 0 16px 0" }}>Other Farmers’ Crops</h2>
        {otherCrops.length === 0 && <p>No other crops available.</p>}
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: 24 }}>
          {otherCrops.map(crop => (
            <div key={crop.id} style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 2px 6px #b2dfdb" }}>
              <h3 style={{ fontSize: 22, fontWeight: 700, color: "#00897b" }}>{crop.name}</h3>
              <p>Quantity: {crop.quantity} {crop.unit}</p>
              <p>Base Price: {crop.base_price} Tk</p>
              {crop.discount && crop.minQuantity && (
                <p style={{ color: "#e65100", fontWeight: 700 }}>
                  {crop.discount}% discount if buy {crop.minQuantity} {crop.unit} or more
                </p>
              )}
              <p>Farmer: {crop.farmer_name}</p>
              <p style={{ color: "#888", fontStyle: "italic" }}>Read-only, cannot add to marketplace</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
