import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";


export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: ""
  });

  const fetchCrops = async () => {
    const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
    const data = await res.json();

    // Filter only farmer's crops
    const myCrops = data.filter(c => c.farmer_id === user.id);
    setCrops(myCrops);
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form)
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "" });
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="marketplace-container">
      <h2>Farmer Marketplace</h2>

      {/* Add Crop Form */}
      <div className="add-crop-form">
        <input
          placeholder="Crop Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          placeholder="Quantity"
          type="number"
          value={form.quantity}
          onChange={e => setForm({ ...form, quantity: e.target.value })}
        />
        <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })}>
          <option value="kg">kg</option>
          <option value="unit">unit</option>
        </select>
        <input
          placeholder="Price per unit/kg"
          type="number"
          value={form.base_price}
          onChange={e => setForm({ ...form, base_price: e.target.value })}
        />
        <button onClick={handleAddCrop}>Add Crop</button>
      </div>

      <h3>Your Crops</h3>
      {crops.length === 0 && <p>No crops added yet.</p>}
      {crops.map(c => (
        <div key={c.id} className="crop-card">
          <strong>{c.name}</strong> - {c.quantity} {c.unit} - Base: {c.base_price} Tk
          <div>Highest Bid: {c.highest_bid || "No bids yet"}</div>

          {c.bids && c.bids.length > 0 && (
            <div className="bids-list">
              <h4>Dealer Bids:</h4>
              <ul>
                {c.bids.map(b => (
                  <li key={b.id}>{b.dealer_name}: {b.bid_price} Tk</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ))}
      <style jsx>{`
  .marketplace-container {
    padding: 30px 20px;
    max-width: 1000px;
    margin: auto;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  }

  h2 {
    text-align: center;
    color: #2c3e50;
    margin-bottom: 30px;
    font-size: 2rem;
    font-weight: 700;
  }

  .add-crop-form {
    display: flex;
    flex-wrap: wrap;
    gap: 15px;
    padding: 20px;
    background-color: #ffffff;
    border-radius: 10px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    margin-bottom: 30px;
    align-items: center;
  }

  .add-crop-form input,
  .add-crop-form select {
    padding: 12px 10px;
    flex: 1 1 150px;
    border-radius: 8px;
    border: 1px solid #ccc;
    font-size: 1rem;
    transition: border-color 0.2s ease;
  }

  .add-crop-form input:focus,
  .add-crop-form select:focus {
    outline: none;
    border-color: #27ae60;
  }

  .add-crop-form button {
    background-color: #27ae60;
    color: #fff;
    border: none;
    padding: 12px 25px;
    border-radius: 8px;
    cursor: pointer;
    font-weight: bold;
    transition: background-color 0.2s ease, transform 0.2s ease;
  }

  .add-crop-form button:hover {
    background-color: #219150;
    transform: translateY(-2px);
  }

  .crop-cards {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
  }

  .crop-card {
    background-color: #fff;
    border-radius: 10px;
    padding: 20px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
  }

  .crop-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(0,0,0,0.15);
  }

  .crop-card strong {
    font-size: 1.2rem;
    color: #27ae60;
    margin-bottom: 5px;
  }

  .bids-list {
    margin-top: 15px;
    padding: 12px;
    border-radius: 8px;
    background-color: #f1f8e9;
  }

  .bids-list h4 {
    margin-bottom: 10px;
    color: #2c3e50;
  }

  .bids-list ul {
    list-style: none;
    padding-left: 0;
  }

  .bids-list li {
    padding: 6px 0;
    border-bottom: 1px solid #e0e0e0;
  }

  .bids-list li:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    .add-crop-form {
      flex-direction: column;
    }
  }
`}</style>

      
    </div>
  );
}
