import { useContext, useEffect, useState } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav('/login');
  };

  // Fetch notifications (dealer requests)
  const fetchNotifications = async () => {
    if (!user || user.role !== "farmer") return;
    try {
      const res = await fetch(`http://localhost:5000/api/requests/${user.id}`, { credentials: "include" });
      const data = await res.json();
      setNotifications(data);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 10000); // auto refresh every 10s
    return () => clearInterval(interval);
  }, [user]);

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>
      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* ðŸ”” Notification Bell */}
            {user.role === "farmer" && (
              <div className="notification-wrapper">
                <button 
                  className="notification-bell" 
                  onClick={() => setOpen(!open)}
                >
                  ðŸ””
                  {notifications.length > 0 && (
                    <span className="badge">{notifications.length}</span>
                  )}
                </button>

                {open && (
                  <div className="notification-dropdown">
                    <h4>Dealer Requests</h4>
                    {notifications.length === 0 ? (
                      <p>No requests yet</p>
                    ) : (
                      notifications.map(r => (
                        <div key={r.id} className="notification-item">
                          <strong>{r.dealer_name}</strong>
                          <p>{r.crop_name} - {r.requested_quantity} {r.unit}</p>
                          <p>Price: {r.bid_price} Tk</p>
                          <small>{r.dealer_phone} | {r.dealer_address}</small>
                        </div>
                      ))
                    )}
                  </div>
                )}
              </div>
            )}

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || '/default-avatar.png'}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
