import { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function DealerDetailsPage() {
  const { user } = useContext(AuthContext);
  const { dealerId, cropId } = useParams();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const fetchRequest = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/dealer/${dealerId}/crop/${cropId}`,
        { credentials: "include" }
      );
      const data = await res.json();
      setRequest(data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching request", err);
      setLoading(false);
    }
  };

  const handleAction = async (status) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/handle/${request.id}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify({ status }),
        }
      );
      const data = await res.json();
      if (status === "accepted") {
        setMessage("ðŸŽ‰ Congratulations! Deal Done.");
        if (data.pdfUrl) {
          window.open(`http://localhost:5000${data.pdfUrl}`, "_blank");
        }
      } else {
        setMessage("âŒ Request Rejected.");
      }
      fetchRequest(); // refresh
    } catch (err) {
      console.error("Error handling request", err);
    }
  };

  useEffect(() => {
    fetchRequest();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (!request) return <p>No request found for this dealer and crop.</p>;

  return (
    <div className="dealer-details-page">
      <h2>Dealer Details</h2>
      <p><strong>Dealer:</strong> {request.dealer_name}</p>
      <p><strong>Phone:</strong> {request.dealer_phone}</p>
      <p><strong>Address:</strong> {request.dealer_address}</p>
      <p><strong>Crop:</strong> {request.crop_name}</p>
      <p><strong>Quantity:</strong> {request.requested_quantity} {request.unit}</p>
      <p><strong>Bid Price:</strong> {request.bid_price} Tk</p>
      <p><strong>Total:</strong> {request.requested_quantity * request.bid_price} Tk</p>

      {request.status === "pending" && user.role === "farmer" && (
        <div className="action-buttons">
          <button className="accept" onClick={() => handleAction("accepted")}>Accept</button>
          <button className="reject" onClick={() => handleAction("rejected")}>Reject</button>
        </div>
      )}

      {message && <div className="popup-message">{message}</div>}
    </div>
  );
}
