// components/Notifications.jsx
import React, { useEffect, useState } from "react";

export default function Notifications({ userId }) {
  const [requests, setRequests] = useState([]);
  const [open, setOpen] = useState(false);

  const fetchRequests = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/${userId}`, { credentials: "include" });
      const data = await res.json();
      setRequests(data);
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  };

  useEffect(() => {
    fetchRequests();
    const interval = setInterval(fetchRequests, 5000); // fetch every 5 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="notification-container">
      <div className="notification-icon" onClick={() => setOpen(!open)}>
        ðŸ”” {requests.length > 0 && <span className="badge">{requests.length}</span>}
      </div>

      {open && (
        <div className="notification-dropdown">
          {requests.length === 0 && <p>No new requests</p>}
          {requests.map(r => (
            <div key={r.id} className="notification-card">
              <p><strong>{r.dealer_name}</strong> requested <strong>{r.requested_quantity} {r.unit}</strong> of <strong>{r.crop_name}</strong></p>
              <p>Bid Price: <strong>{r.bid_price} Tk</strong></p>
              <div className="action-buttons">
                <button className="accept">Accept</button>
                <button className="reject">Reject</button>
              </div>
            </div>
          ))}
        </div>
      )}

      <style jsx>{`
        .notification-container {
          position: relative;
          margin-left: auto;
        }
        .notification-icon {
          cursor: pointer;
          position: relative;
          font-size: 1.5rem;
        }
        .badge {
          position: absolute;
          top: -5px;
          right: -10px;
          background: red;
          color: white;
          border-radius: 50%;
          padding: 2px 6px;
          font-size: 0.75rem;
        }
        .notification-dropdown {
          position: absolute;
          right: 0;
          top: 30px;
          background: white;
          border: 1px solid #ddd;
          box-shadow: 0 3px 10px rgba(0,0,0,0.1);
          width: 300px;
          max-height: 400px;
          overflow-y: auto;
          border-radius: 8px;
          z-index: 1000;
        }
        .notification-card {
          padding: 10px;
          border-bottom: 1px solid #eee;
        }
        .notification-card:last-child { border-bottom: none; }
        .action-buttons {
          display: flex;
          gap: 8px;
          margin-top: 6px;
        }
        .accept {
          background: #28a745;
          color: white;
          border: none;
          padding: 4px 8px;
          border-radius: 4px;
          cursor: pointer;
        }
        .reject {
          background: #dc3545;
          color: white;
          border: none;
          padding: 4px 8px;
          border-radius: 4px;
          cursor: pointer;
        }
      `}</style>
    </div>
  );
}
