// frontend/src/pages/FarmerMarketplace.jsx
import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  
  // Crops
  const [farmerCrops, setFarmerCrops] = useState([]);
  const [marketplaceCrops, setMarketplaceCrops] = useState([]);
  
  // Add Crop form
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: "",
    crop_pic: "",
    discount: "",
    minQuantity: ""
  });
  const [picPreview, setPicPreview] = useState("");
  const [showAddModal, setShowAddModal] = useState(false);

  // Fetch crops
  const fetchFarmerCrops = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/crops/farmer/${user.id}`, { credentials: "include" });
      const data = await res.json();
      setFarmerCrops(data);
    } catch (err) {
      console.error("Error fetching farmer crops", err);
    }
  };

  const fetchMarketplaceCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setMarketplaceCrops(data);
    } catch (err) {
      console.error("Error fetching marketplace crops", err);
    }
  };

  useEffect(() => {
    if (user?.id) {
      fetchFarmerCrops();
      fetchMarketplaceCrops();
    }
  }, [user]);

  // Handle image upload
  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(f => ({ ...f, crop_pic: reader.result }));
        setPicPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Add crop to farmer's crops
  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/crops/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          name: form.name,
          land_size: form.quantity, // use quantity as land_size temporarily
          cultivate_date: new Date().toISOString().split('T')[0],
          harvest_date: new Date().toISOString().split('T')[0],
          total_cost: form.base_price,
          crop_pic: form.crop_pic
        })
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "", crop_pic: "", discount: "", minQuantity: "" });
      setPicPreview("");
      setShowAddModal(false);
      fetchFarmerCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  // Harvest crop to marketplace
  const handleHarvest = async (cropId, quantity, price) => {
    try {
      await fetch(`http://localhost:5000/api/crops/harvest/${cropId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ quantity, price })
      });
      fetchFarmerCrops();
      fetchMarketplaceCrops();
      alert("Crop harvested to marketplace!");
    } catch (err) {
      console.error(err);
      alert("Error harvesting crop");
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: `url('https://cdn.pixabay.com/photo/2020/06/02/05/39/barley-5249498_1280.jpg') center/cover no-repeat, linear-gradient(135deg, #e0f7fa 0%, #f9fbe7 100%)`, padding: '32px 0' }}>
      <div style={{ maxWidth: 1200, margin: '0 auto', padding: '24px 0' }}>
        {/* Navbar */}
        <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32 }}>
          <h2 style={{ fontSize: 32, fontWeight: 700, color: '#26a69a' }}>ðŸŒ¾ Farmer Marketplace</h2>
          <button style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '10px 24px', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', boxShadow: '0 2px 8px #b2dfdb' }} onClick={() => setShowAddModal(true)}>
            <span style={{ fontSize: 20, marginRight: 8 }}>âž•</span> Add Crop
          </button>
        </nav>

        {/* Add Crop Modal */}
        {showAddModal && (
          <div style={{ position: 'fixed', top:0, left:0, width:'100vw', height:'100vh', background:'rgba(0,0,0,0.3)', display:'flex', alignItems:'center', justifyContent:'center', zIndex:1000 }}>
            <div style={{ background:'#fff', borderRadius:18, padding:36, maxWidth:420, width:'100%', position:'relative' }}>
              <button style={{ position:'absolute', top:12, right:12, background:'#e57373', color:'#fff', border:'none', borderRadius:6, padding:'4px 10px', fontWeight:600, cursor:'pointer' }} onClick={() => setShowAddModal(false)}>X</button>
              <h3 style={{ fontWeight:700, marginBottom:18, color:'#00897b', fontSize:24 }}>Add New Crop</h3>
              <form onSubmit={e => { e.preventDefault(); handleAddCrop(); }}>
                <div style={{ marginBottom:16 }}>
                  <label style={{ display:'block', fontWeight:500, color:'#00897b', marginBottom:6 }}>Crop Name</label>
                  <input placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} style={{ width:'100%', padding:'12px 16px', borderRadius:10, border:'1px solid #b2dfdb', fontSize:17 }} required />
                </div>
                <div style={{ marginBottom:16 }}>
                  <label style={{ display:'block', fontWeight:500, color:'#00897b', marginBottom:6 }}>Quantity / Land Size</label>
                  <input type="number" value={form.quantity} onChange={e => setForm({ ...form, quantity: e.target.value })} style={{ width:'100%', padding:'12px 16px', borderRadius:10, border:'1px solid #b2dfdb', fontSize:17 }} required />
                </div>
                <div style={{ marginBottom:16 }}>
                  <label style={{ display:'block', fontWeight:500, color:'#00897b', marginBottom:6 }}>Base Price</label>
                  <input type="number" value={form.base_price} onChange={e => setForm({ ...form, base_price: e.target.value })} style={{ width:'100%', padding:'12px 16px', borderRadius:10, border:'1px solid #b2dfdb', fontSize:17 }} required />
                </div>
                <div style={{ marginBottom:16 }}>
                  <label style={{ display:'block', fontWeight:500, color:'#00897b', marginBottom:6 }}>Crop Image</label>
                  <input type="file" accept="image/*" onChange={handlePicChange} style={{ width:'100%' }} />
                  {picPreview && <img src={picPreview} alt="Preview" style={{ maxWidth:120, marginTop:8, borderRadius:10, boxShadow:'0 2px 8px #b2dfdb' }} />}
                </div>
                <button type="submit" style={{ background:'#26c6da', color:'#fff', border:'none', borderRadius:10, padding:'14px 0', fontWeight:'bold', fontSize:18, cursor:'pointer', width:'100%' }}>Add Crop</button>
              </form>
            </div>
          </div>
        )}

        {/* Farmer Crops */}
        <div style={{ marginTop:24 }}>
          <h3 style={{ fontWeight:600, marginBottom:16, color:'#00897b' }}>ðŸŒ± Your Crops (Not in Marketplace)</h3>
          {farmerCrops.length === 0 ? <p>No crops yet.</p> : (
            <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(300px,1fr))', gap:20 }}>
              {farmerCrops.map(crop => (
                <div key={crop.id} style={{ padding:16, border:'1px solid #ccc', borderRadius:12 }}>
                  <h4>{crop.name}</h4>
                  <p>Quantity: {crop.quantity} {crop.unit}</p>
                  <p>Base Price: {crop.base_price} Tk</p>
                  <button onClick={() => handleHarvest(crop.id, crop.quantity, crop.base_price)} style={{ marginTop:10, background:'#43a047', color:'#fff', borderRadius:8, padding:'6px 12px', cursor:'pointer' }}>Harvest to Marketplace</button>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Marketplace Crops */}
        <div style={{ marginTop:40 }}>
          <h3 style={{ fontWeight:600, marginBottom:16, color:'#00897b' }}>ðŸŒ¾ Marketplace Crops</h3>
          {marketplaceCrops.length === 0 ? <p>No crops in marketplace.</p> : (
            <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(320px,1fr))', gap:32 }}>
              {marketplaceCrops.map(crop => (
                <div key={crop.id} style={{ padding:16, border:'1px solid #ccc', borderRadius:12 }}>
                  <h4>{crop.name}</h4>
                  <p>Quantity: {crop.quantity} {crop.unit}</p>
                  <p>Price: {crop.price || crop.base_price} Tk</p>
                </div>
              ))}
            </div>
          )}
        </div>

      </div>
    </div>
  );
}
