import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({ name: "", quantity: "", unit: "kg", base_price: "" });

  // Fetch crops for this farmer
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setCrops(data.filter(c => c.farmer_id === user.id));
    } catch (err) {
      console.error(err);
    }
  };

  // Add new crop
  const handleAddCrop = async () => {
    if (!form.name || !form.quantity || !form.base_price) return alert("Please fill all fields");

    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form),
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "" });
      fetchCrops();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete crop
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this crop?")) return;
    try {
      await fetch(`http://localhost:5000/api/marketplace/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      fetchCrops();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="farmer-marketplace-container">
      <h2>Your Marketplace</h2>

      {/* Add Crop Form */}
      <div className="add-crop-form">
        <input
          placeholder="Crop Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={form.quantity}
          onChange={e => setForm({ ...form, quantity: e.target.value })}
        />
        <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })}>
          <option value="kg">kg</option>
          <option value="unit">unit</option>
        </select>
        <input
          type="number"
          placeholder="Price per unit/kg"
          value={form.base_price}
          onChange={e => setForm({ ...form, base_price: e.target.value })}
        />
        <button className="btn-add" onClick={handleAddCrop}>Add Crop</button>
      </div>

      {/* Crop List */}
      <div className="crop-cards">
        {crops.length === 0 ? (
          <p>No crops added yet.</p>
        ) : (
          crops.map(c => (
            <div key={c.id} className="crop-card">
              <h3>{c.name}</h3>
              <p>
                Quantity: {c.quantity} {c.unit}<br/>
                Base Price: {c.base_price} Tk<br/>
                Highest Bid: {c.highest_bid || "No bids yet"}
              </p>
              <button className="btn-delete" onClick={() => handleDelete(c.id)}>Delete</button>
            </div>
          ))
        )}
      </div>

      {/* Styles */}
      <style jsx>{`
        .farmer-marketplace-container {
          padding: 20px;
          max-width: 900px;
          margin: auto;
        }
        .add-crop-form {
          display: flex;
          gap: 10px;
          margin-bottom: 20px;
          flex-wrap: wrap;
        }
        .add-crop-form input, .add-crop-form select {
          padding: 8px;
          flex: 1 1 150px;
        }
        .btn-add {
          background-color: #28a745;
          color: white;
          border: none;
          padding: 10px 15px;
          cursor: pointer;
          border-radius: 5px;
        }
        .crop-cards {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
          gap: 15px;
        }
        .crop-card {
          border: 1px solid #ccc;
          border-radius: 8px;
          padding: 15px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        .btn-delete {
          background-color: #dc3545;
          color: white;
          border: none;
          padding: 6px 10px;
          cursor: pointer;
          border-radius: 5px;
          margin-top: 10px;
        }
      `}</style>
    </div>
  );
}
