import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch notifications once on mount or user change
  useEffect(() => {
    if (!user) return;

    const fetchNotifications = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/requests/notifications/${user.id}`,
          { credentials: "include" }
        );
        const data = await res.json();

        // Merge server data with local read_flag to avoid overwriting read notifications
        setNotifications((prev) => {
          const prevMap = {};
          prev.forEach((n) => {
            prevMap[n.id] = n.read_flag;
          });

          return data.map((n) => ({
            ...n,
            read_flag: prevMap[n.id] !== undefined ? prevMap[n.id] : n.read_flag,
          }));
        });
      } catch (err) {
        console.error("Error fetching notifications", err);
      }
    };

    fetchNotifications();
  }, [user]);

  const handleClickNotification = async (n) => {
    nav(`/dealer/${n.dealer_id}/request/${n.request_id}`);

    if (!n.read_flag) {
      try {
        await fetch(`http://localhost:5000/api/notifications/read/${n.id}`, {
          method: "POST",
          credentials: "include",
        });

        // update local state
        setNotifications((prev) =>
          prev.map((item) =>
            item.id === n.id ? { ...item, read_flag: 1 } : item
          )
        );
      } catch (err) {
        console.error("Failed to mark notification as read", err);
      }
    }

    setOpen(false);
  };

  const unreadCount = notifications.filter((n) => !n.read_flag).length;

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>

      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* Notification Bell */}
            <div className="notification-wrapper relative">
              <button
                className="notification-bell relative"
                onClick={() => setOpen(!open)}
              >
                🔔
                {unreadCount > 0 && (
                  <span className="badge absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold px-2 rounded-full">
                    {unreadCount}
                  </span>
                )}
              </button>

              {open && (
                <div className="notification-dropdown absolute right-0 mt-2 w-80 bg-white shadow-lg rounded z-50">
                  <h4 className="p-2 font-bold border-b">Notifications</h4>
                  {notifications.length === 0 ? (
                    <p className="p-3 text-sm">No notifications</p>
                  ) : (
                    notifications.map((n) => {
                      const createdAt = new Date(n.created_at);
                      const now = new Date();
                      const diffMinutes = Math.floor((now - createdAt) / 60000);
                      const timeAgo =
                        diffMinutes < 1
                          ? "just now"
                          : diffMinutes < 60
                          ? `${diffMinutes} minutes ago`
                          : diffMinutes < 1440
                          ? `${Math.floor(diffMinutes / 60)} hours ago`
                          : `${Math.floor(diffMinutes / 1440)} days ago`;

                      return (
                        <div
                          key={n.id}
                          onClick={() => handleClickNotification(n)}
                          className={`p-3 border-b cursor-pointer rounded ${
                            n.read_flag
                              ? "bg-white text-black"
                              : "bg-blue-600 text-white font-bold"
                          }`}
                        >
                          <span className="block">
                            You have a new request for{" "}
                            <strong>{n.crop_name}</strong> {n.requested_quantity} {n.unit} at {n.bid_price} Tk
                          </span>
                          <span className="text-xs">
                            {timeAgo} {!n.read_flag && <span className="ml-2 text-yellow-200">● New</span>}
                          </span>
                        </div>
                      );
                    })
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
