import { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const { login, getUserRole } = useContext(AuthContext); // getUserRole to fetch role from backend
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      // Perform login
      const userData = await login(form.email, form.password); 
      // userData should include user id

      // Check role from backend using userId
      const role = await getUserRole(userData.id);

      if (role === "farmer") {
        navigate("/farmer-home"); // Farmer Homepage
      } else {
        setError("Please login as a farmer or create a farmer account");
      }
    } catch (err) {
      setError("Invalid email or password");
    }
  };

  return (
    <div className="auth-bg">
      <div className="auth-form-container">
        <h2>Welcome Back ðŸŒ¾</h2>
        <p>Login to continue your AgroVision journey</p>

        {error && <div className="error-box">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            value={form.email}
            onChange={handleChange}
            required
          />

          <label>Password</label>
          <input
            type="password"
            name="password"
            placeholder="Enter your password"
            value={form.password}
            onChange={handleChange}
            required
          />

          <button type="submit">Login</button>
        </form>

        <p className="switch-link">
          Donâ€™t have an account? <a href="/register">Register here</a>
        </p>
      </div>
    </div>
  );
}
