import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch notifications
  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/requests/notifications/${user.id}`,
        { credentials: "include" }
      );
      const data = await res.json();
      setNotifications(data);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // poll every 5s
    return () => clearInterval(interval);
  }, [user]);

  // Mark as read + navigate
  const handleClick = async (n) => {
    nav(`/dealer/${n.dealer_id}/request/${n.request_id}`);

    if (!n.read_flag) {
      try {
        // Update backend (mark read permanently)
        await fetch(`http://localhost:5000/api/notifications/read/${n.id}`, {
          method: "POST",
          credentials: "include",
        });

        // Update frontend state
        setNotifications((prev) =>
          prev.map((item) =>
            item.id === n.id ? { ...item, read_flag: 1 } : item
          )
        );
      } catch (err) {
        console.error("Failed to mark notification as read", err);
      }
    }
  };

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>
      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* ðŸ”” Notification Bell */}
            <div className="notification-wrapper">
              <button
                className="notification-bell"
                onClick={() => setOpen(!open)}
              >
                ðŸ””
                {notifications.filter((n) => !n.read_flag).length > 0 && (
                  <span className="badge">
                    {notifications.filter((n) => !n.read_flag).length}
                  </span>
                )}
              </button>

              {open && (
                <div className="notification-dropdown">
                  <h4>Notifications</h4>
                  {notifications.length === 0 ? (
                    <p>No new notifications</p>
                  ) : (
                    notifications.map((n) => {
                      const isUnread = !n.read_flag;
                      const createdAt = new Date(n.created_at);
                      const timeAgo = isNaN(createdAt)
                        ? ""
                        : createdAt.toLocaleString();

                      return (
                        <div
                          key={n.id}
                          className={`notification-item ${
                            isUnread ? "unread" : "read"
                          }`}
                          onClick={() => handleClick(n)}
                        >
                          <span className="notification-text">
                            You have a new request for{" "}
                            <strong>{n.crop_name}</strong>:{" "}
                            {n.requested_quantity} {n.unit} at {n.bid_price} Tk
                          </span>
                          <span className="notification-meta">
                            {timeAgo}
                            {isUnread && (
                              <span className="new-badge">New</span>
                            )}
                          </span>
                        </div>
                      );
                    })
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
