import { useContext, useEffect, useState } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav('/login');
  };

  // Fetch notifications only if farmer is logged in
  useEffect(() => {
    if (user?.role === 'farmer') {
      fetch('http://localhost:5000/api/notifications', { credentials: 'include' })
        .then((res) => res.json())
        .then((data) => setNotifications(data))
        .catch((err) => console.error(err));
    }
  }, [user]);

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>

      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* Farmer Notifications */}
            {user.role === 'farmer' && (
              <div className="notification-wrapper">
                <button className="notif-btn" onClick={() => setOpen(!open)}>
                  üîî
                  {notifications.length > 0 && (
                    <span className="notif-count">{notifications.length}</span>
                  )}
                </button>

                {open && (
                  <div className="notif-dropdown">
                    <h4>Notifications</h4>
                    {notifications.length === 0 ? (
                      <p>No new requests</p>
                    ) : (
                      notifications.map((n, i) => (
                        <div key={i} className="notif-item">
                          <p><strong>{n.dealer_name}</strong> requested {n.quantity} {n.unit}</p>
                          <p>üí∞ {n.bid_price} Tk</p>
                          <p>üìç {n.dealer_address}</p>
                          <p>üìû {n.dealer_phone}</p>
                        </div>
                      ))
                    )}
                  </div>
                )}
              </div>
            )}

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || '/default-avatar.png'}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
