import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

export default function Profile() {
  const { user, updateProfile } = useContext(AuthContext);
  const [name, setName] = useState(user?.name || "");
  const [profilePic, setProfilePic] = useState(user?.profile_pic || "");
  const [preview, setPreview] = useState("");

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Here you can later upload to Cloudinary / local server
      const url = URL.createObjectURL(file);
      setPreview(url);

      // Temporary: just use base64 for now
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfilePic(reader.result); // base64 string
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      await updateProfile(name, profilePic);
      alert("Profile updated successfully!");
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2>My Profile</h2>

        <div className="profile-pic-wrapper">
          <img
            src={preview || profilePic || "https://via.placeholder.com/150"}
            alt="Profile"
            className="profile-pic"
          />
          <label className="upload-btn">
            Change Photo
            <input type="file" accept="image/*" onChange={handleFileChange} hidden />
          </label>
        </div>

        <div className="profile-info">
          <label>Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <label>Email</label>
          <input type="text" value={user.email} disabled />

          <label>Role</label>
          <input type="text" value={user.role} disabled />
        </div>

        <button className="save-btn" onClick={handleSave}>
          Save Changes
        </button>
      </div>
    </div>
  );
}
