import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function DealerDetails() {
  const { dealerId } = useParams();
  const [details, setDetails] = useState(null);

  const fetchDealerDetails = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/dealer/${dealerId}`, { credentials: "include" });
      const data = await res.json();
      setDetails(data);
    } catch (err) {
      console.error("Error fetching dealer details", err);
    }
  };

  useEffect(() => {
    fetchDealerDetails();
  }, [dealerId]);

  if (!details) return <p>Loading...</p>;

  return (
    <div className="dealer-details-page">
      <h2>Dealer Details: {details.dealer_name}</h2>
      <p><strong>Address:</strong> {details.dealer_address || "N/A"}</p>
      <p><strong>Phone:</strong> {details.dealer_phone || "N/A"}</p>

      <h3>Requested Crops</h3>
      <ul>
        {details.requests.map(r => (
          <li key={r.id}>
            <strong>{r.crop_name}</strong> - {r.requested_quantity} {r.unit} - Total Price: {r.bid_price * r.requested_quantity} Tk
          </li>
        ))}
      </ul>
    </div>
  );
}
