import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [requests, setRequests] = useState([]);

  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: ""
  });

  // Fetch farmer's crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
      const data = await res.json();
      const myCrops = data.filter(c => c.farmer_id == user.id);
      setCrops(myCrops);
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  // Fetch requests for farmer
  const fetchRequests = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/farmer/${user.id}`, { credentials: 'include' });
      const data = await res.json();
      setRequests(data);
    } catch (err) {
      console.error("Error fetching requests", err);
    }
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form)
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "" });
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  // Accept or Reject a request (updates request status)
  const handleRequestAction = async (requestId, status) => {
    try {
      await fetch(`http://localhost:5000/api/requests/handle/${requestId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ status }),
      });
      fetchCrops();      // refresh highest bids
      fetchRequests();   // refresh notifications
    } catch (err) {
      console.error("Error handling request", err);
    }
  };

  useEffect(() => {
  fetchCrops();
  fetchRequests();
  const interval = setInterval(() => {
    fetchCrops();
    fetchRequests();
  }, 5000);
  return () => clearInterval(interval);
}, []);

  return (
    <div className="farmer-marketplace-container">
      <h2>ðŸŒ¾ Farmer Marketplace</h2>

      {/* Add Crop Form */}
      <div className="add-crop-form">
        <input
          placeholder="Crop Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          placeholder="Quantity"
          type="number"
          value={form.quantity}
          onChange={e => setForm({ ...form, quantity: e.target.value })}
        />
        <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })}>
          <option value="kg">kg</option>
          <option value="unit">unit</option>
        </select>
        <input
          placeholder="Price per unit/kg"
          type="number"
          value={form.base_price}
          onChange={e => setForm({ ...form, base_price: e.target.value })}
        />
        <button onClick={handleAddCrop}>+ Add Crop</button>
      </div>

      {/* Crops Section */}
      <div className="crops-section">
        <h3>Your Crops</h3>
        {crops.length === 0 && <p>No crops added yet.</p>}

        <div className="crop-cards">
          {crops.map(c => (
            <div key={c.id} className="crop-card">
              <div className="crop-info">
                <h3>{c.name}</h3>
                <p>Quantity: {c.quantity} {c.unit}</p>
                <p>Base Price: {c.base_price} Tk</p>
                <p>Highest Bid: {c.highest_bid || "No bids yet"}</p>
              </div>

              {requests.filter(r => r.crop_id === c.id).length > 0 && (
                <div className="requests-list">
                  <h4>Dealer Requests</h4>
                  <ul>
                    {requests.filter(r => r.crop_id === c.id).map(r => (
                      <li key={r.id}>
                        <span>{r.dealer_name} requested {r.requested_quantity} {r.unit}</span>
                        <strong> at {r.bid_price} Tk</strong>
                        <div className="action-buttons">
                          <button className="accept" onClick={() => handleRequestAction(r.id, "accepted")}>Accept</button>
                          <button className="reject" onClick={() => handleRequestAction(r.id, "rejected")}>Reject</button>
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
