import { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function DealerDetailsPage() {
  const { user } = useContext(AuthContext);
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchRequest = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/${requestId}`, {
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) setRequest(data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching request", err);
    }
  };

  const handleAction = async (status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/handle/${requestId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ status }),
      });
      const data = await res.json();
      if (status === "accepted") {
        alert("üéâ Congratulations, deal done!");
        if (data.receipt_url) {
          window.open(`http://localhost:5000${data.receipt_url}`, "_blank");
        }
      } else {
        alert("‚ùå You rejected this request.");
      }
    } catch (err) {
      console.error("Error handling action", err);
    }
  };

  useEffect(() => {
    fetchRequest();
  }, [requestId]);

  if (loading) return <p>Loading...</p>;
  if (!request) return <p>No request found.</p>;

  return (
    <div className="dealer-details">
      <h2>Dealer Request Details</h2>
      <p><strong>Dealer:</strong> {request.dealer_name}</p>
      <p><strong>Phone:</strong> {request.dealer_phone}</p>
      <p><strong>Address:</strong> {request.dealer_address}</p>
      <p><strong>Crop:</strong> {request.crop_name}</p>
      <p><strong>Quantity:</strong> {request.requested_quantity} {request.unit}</p>
      <p><strong>Bid Price:</strong> {request.bid_price} Tk</p>

      {user.role === "farmer" && request.status === "pending" && (
        <div className="actions">
          <button className="accept" onClick={() => handleAction("accepted")}>
            ‚úÖ Accept
          </button>
          <button className="reject" onClick={() => handleAction("rejected")}>
            ‚ùå Reject
          </button>
        </div>
      )}
    </div>
  );
}
