//frontend/src/pages/FarmerHomePage.jsx
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import FarmerCropPage from "./FarmerCropPage"; // Farmer crop management

export default function FarmerHomePage() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  if (!user || user.role !== "farmer") {
    return <p>Access denied. You are not a farmer.</p>;
  }

  const handleMarketplaceClick = () => {
    navigate("/farmer-marketplace"); // existing marketplace route
  };

  const handleKnowledgeClick = () => {
    navigate("/knowledge"); // existing guides route
  };

  return (
    <div>
      {/* Hero Section */}
      <header className="hero">
        <div className="hero-overlay">
          <h1>
            Welcome back, <span className="highlight">{user.name}</span> ðŸŒ¾
          </h1>
          <p>Manage your crops, sell at the marketplace, and learn more!</p>
        </div>
      </header>

      {/* Features Section */}
      <main className="container">
        <h2 className="section-title">Your Dashboard</h2>
        <section className="cards">
          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Marketplace"
              alt="Marketplace"
              className="card-img"
            />
            <h3>Marketplace</h3>
            <p>Sell your crops and connect with buyers.</p>
            <button onClick={handleMarketplaceClick}>Go to Marketplace</button>
          </div>

          <div className="card">
            <img
              src="https://via.placeholder.com/300x180?text=Knowledge+Center"
              alt="Knowledge"
              className="card-img"
            />
            <h3>Knowledge Center</h3>
            <p>Access crop guides, tips, and weather forecasts.</p>
            <button onClick={handleKnowledgeClick}>Open Guides</button>
          </div>
        </section>

        {/* My Crops Section */}
        <section className="my-crops mt-8">
          <h2 className="section-title">My Crops</h2>
          <FarmerCropPage />
        </section>
      </main>

      {/* Footer */}
      <footer className="site-footer">
        <div>
          Â© {new Date().getFullYear()} AgroVision â€” Contact: +880 xxxxxxxx
        </div>
      </footer>
    </div>
  );
}
