// frontend/src/pages/FarmerMarketplace.jsx
import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: "",
    crop_pic: "",
    discount: "",
    minQuantity: ""
  });
  const [picPreview, setPicPreview] = useState("");
  const [showAddModal, setShowAddModal] = useState(false);
  const [showOfferModal, setShowOfferModal] = useState(false);
  const [offerForm, setOfferForm] = useState({ price: "", quantity: "", discount: "", minQuantity: "" });
  const [selectedCrop, setSelectedCrop] = useState(null);

  // Fetch all crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/crops", { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  useEffect(() => {
    fetchCrops();
  }, [user]);

  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(f => ({ ...f, crop_pic: reader.result }));
        setPicPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Add crop (tracking only, not marketplace)
  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/crops/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          name: form.name,
          land_size: 0,           // replace with actual value if needed
          cultivate_date: new Date().toISOString(),
          harvest_date: new Date().toISOString(),
          total_cost: form.base_price,
          crop_pic: form.crop_pic
        })
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "", crop_pic: "", discount: "", minQuantity: "" });
      setPicPreview("");
      setShowAddModal(false);
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  // Add crop to marketplace (explicit action)
  const addToMarketplace = async (crop) => {
    try {
      await fetch(`http://localhost:5000/api/crops/harvest/${crop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          price: crop.base_price,
          quantity: crop.quantity,
          discount: crop.discount,
          minQuantity: crop.minQuantity
        })
      });
      alert("Crop added to marketplace!");
      fetchCrops();
    } catch (err) {
      alert("Error adding to marketplace.");
    }
  };

  // Offer modal
  const openOfferModal = (crop) => {
    setSelectedCrop(crop);
    setOfferForm({ price: crop.base_price, quantity: "" });
    setShowOfferModal(true);
  };

  const handleOfferSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch(`http://localhost:5000/api/crops/request/${selectedCrop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          requested_quantity: offerForm.quantity,
          bid_price: offerForm.price,
          dealer_phone: user.phone || "",
          dealer_address: user.address || "",
          unit: selectedCrop.unit
        })
      });
      setShowOfferModal(false);
      setOfferForm({ price: "", quantity: "" });
      setSelectedCrop(null);
      fetchCrops();
      alert("Offer submitted!");
    } catch (err) {
      alert("Error submitting offer.");
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: `url('https://cdn.pixabay.com/photo/2020/06/02/05/39/barley-5249498_1280.jpg') center/cover no-repeat, linear-gradient(135deg, #e0f7fa 0%, #f9fbe7 100%)`, padding: '32px 0' }}>
      <div style={{ maxWidth: 1200, margin: '0 auto', padding: '24px 0' }}>
        <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32 }}>
          <h2 style={{ fontSize: 32, fontWeight: 700, color: '#26a69a' }}>ðŸŒ¾ Farmer Marketplace</h2>
          <button style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '10px 24px', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', boxShadow: '0 2px 8px #b2dfdb' }} onClick={() => setShowAddModal(true)}>
            <span style={{ fontSize: 20, marginRight: 8 }}>âž•</span> Add Crop
          </button>
        </nav>

        {/* Add Crop Modal */}
        {showAddModal && (
          <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
            <div style={{ background: '#fff', borderRadius: 18, boxShadow: '0 4px 18px #b2dfdb', padding: 36, maxWidth: 420, width: '100%', position: 'relative' }}>
              <button style={{ position: 'absolute', top: 12, right: 12, background: '#e57373', color: '#fff', border: 'none', borderRadius: 6, padding: '4px 10px', fontWeight: 600, cursor: 'pointer' }} onClick={() => setShowAddModal(false)}>X</button>
              <h3 style={{ fontWeight: 700, marginBottom: 18, color: '#00897b', fontSize: 24 }}>Add New Crop</h3>
              <form onSubmit={e => { e.preventDefault(); handleAddCrop(); }}>
                <div style={{ marginBottom: 16 }}>
                  <label>Crop Name</label>
                  <input placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} required style={{ width: '100%', padding: 12, borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17 }} />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label>Price per unit/kg</label>
                  <input type="number" placeholder="Price" value={form.base_price} onChange={e => setForm({ ...form, base_price: e.target.value })} required style={{ width: '100%', padding: 12, borderRadius: 10, border: '1px solid #b2dfdb', fontSize: 17 }} />
                </div>
                <div style={{ marginBottom: 16 }}>
                  <label>Crop Image</label>
                  <input type="file" accept="image/*" onChange={handlePicChange} style={{ width: '100%' }} />
                  {picPreview && <img src={picPreview} alt="Preview" style={{maxWidth:120, marginTop:8, borderRadius:10}} />}
                </div>
                <button type="submit" style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: 14, fontWeight: 'bold', fontSize: 18, cursor: 'pointer', width: '100%' }}>Add Crop</button>
              </form>
            </div>
          </div>
        )}

        {/* Offer Modal */}
        {showOfferModal && selectedCrop && (
          <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
            <div style={{ background: '#fff', borderRadius: 18, padding: 36, maxWidth: 420, width: '100%', position: 'relative' }}>
              <button style={{ position: 'absolute', top: 12, right: 12, background: '#e57373', color: '#fff', border: 'none', borderRadius: 6, padding: 4, cursor: 'pointer' }} onClick={() => setShowOfferModal(false)}>X</button>
              <h3>Add Offer for {selectedCrop.name}</h3>
              <form onSubmit={handleOfferSubmit}>
                <input type="number" placeholder="Offer Price" value={offerForm.price} onChange={e => setOfferForm({ ...offerForm, price: e.target.value })} required style={{ width: '100%', padding: 12, borderRadius: 10, border: '1px solid #b2dfdb', marginBottom: 12 }} />
                <input type="number" placeholder="Quantity" value={offerForm.quantity} onChange={e => setOfferForm({ ...offerForm, quantity: e.target.value })} required style={{ width: '100%', padding: 12, borderRadius: 10, border: '1px solid #b2dfdb', marginBottom: 12 }} />
                <button type="submit" style={{ background: '#43a047', color: '#fff', borderRadius: 10, padding: 14, width: '100%' }}>Submit Offer</button>
              </form>
            </div>
          </div>
        )}

        {/* Crop Grid */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: 32 }}>
          {crops.map(crop => (
            <div key={crop.id} style={{ background: '#fff', borderRadius: 18, boxShadow: '0 4px 12px #b2dfdb', overflow: 'hidden', display: 'flex', flexDirection: 'column' }}>
              <img src={crop.crop_pic || 'https://via.placeholder.com/320x200'} alt={crop.name} style={{ width: '100%', height: 200, objectFit: 'cover' }} />
              <div style={{ padding: 16, flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                <h3 style={{ marginBottom: 6 }}>{crop.name}</h3>
                <p>Farmer: {crop.farmer_name}</p>
                <p>Base Price: {crop.base_price} Tk / {crop.unit}</p>
                <p>Highest Bid: {crop.highest_bid || 0} Tk</p>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 12 }}>
                  <button onClick={() => openOfferModal(crop)} style={{ background: '#26c6da', color: '#fff', borderRadius: 10, padding: 10, flex: 1, marginRight: 8 }}>Offer</button>
                  <button onClick={() => addToMarketplace(crop)} style={{ background: '#43a047', color: '#fff', borderRadius: 10, padding: 10, flex: 1 }}>Add to Marketplace</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
