//frontend/src/pages/DealerMarketplace.jsx
import { useEffect, useState, useContext } from "react";
import  {AuthContext}   from "../context/AuthContext";

export default function DealerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [bidValues, setBidValues] = useState({});
  const [successMsg, setSuccessMsg] = useState(""); // ✅ success message

  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  const handleBid = async (cropId, bidPrice, quantity, basePrice) => {
    if (!bidPrice || !quantity) return alert("Enter quantity and bid");
    const minBid = basePrice - 5;
const maxBid = basePrice + 5;
if (bidPrice < minBid || bidPrice > maxBid) {
  return alert(`Bid must be between ${minBid} and ${maxBid}`);
}

    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/request/${cropId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          requested_quantity: parseInt(quantity),
          bid_price: parseFloat(bidPrice),
          dealer_phone: user.phone || "",
          dealer_address: user.address || "",
          unit: "kg"
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);

      // Show popup success message
      setSuccessMsg("Request sent successfully!");
      setTimeout(() => setSuccessMsg(""), 3000); // hide after 3s

      fetchCrops();
      setBidValues({ ...bidValues, [cropId]: "", [`${cropId}_qty`]: "" });
    } catch (err) {
      console.error(err);
      alert("Error sending request");
    }
  };

  useEffect(() => {
    fetchCrops();
    const interval = setInterval(fetchCrops, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dealer-marketplace-container" style={{ background: '#f9fbe7', minHeight: '100vh', padding: '32px 0', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', fontSize: 36, fontWeight: 700, color: '#26a69a', marginBottom: 32 }}>Dealer Marketplace</h2>

      {successMsg && (
        <div className="popup-success" style={{ background: '#43a047', color: '#fff', borderRadius: 8, padding: '12px 24px', fontWeight: 600, fontSize: 18, textAlign: 'center', marginBottom: 24, boxShadow: '0 2px 8px #b2dfdb' }}>
          {successMsg}
        </div>
      )}

      {crops.length === 0 && <p style={{ textAlign: 'center', color: '#888', fontSize: 20 }}>No crops available yet.</p>}

      <div className="crop-cards" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(340px, 1fr))', gap: 32, maxWidth: 1200, margin: '0 auto' }}>
        {crops.map(c => (
          <div key={c.id} className="crop-card" style={{ background: '#fff', borderRadius: 18, boxShadow: '0 4px 18px #b2dfdb', padding: 28, display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: 320, position: 'relative', transition: 'box-shadow 0.2s', cursor: 'pointer' }}>
            <div className="crop-info" style={{ width: '100%', textAlign: 'center' }}>
              <h3 style={{ fontSize: 26, fontWeight: 700, color: '#26a69a', marginBottom: 10 }}>{c.name} {c.discount && c.minQuantity ? <span title="Special Offer" style={{marginLeft:8}}>🎁</span> : null}</h3>
              <p style={{ fontSize: 17, margin: '6px 0' }}>Farmer: <span style={{ color: '#00897b', fontWeight: 600 }}>{c.farmer_name || "Unknown"}</span></p>
              <p style={{ fontSize: 17, margin: '6px 0' }}>Quantity: <span style={{ color: '#00897b', fontWeight: 600 }}>{c.quantity} {c.unit}</span></p>
              <p style={{ fontSize: 17, margin: '6px 0' }}>Base Price: <span style={{ color: '#00897b', fontWeight: 600 }}>{c.base_price} Tk</span></p>
              <p style={{ fontSize: 17, margin: '6px 0' }}>Highest Bid: <span style={{ color: '#00897b', fontWeight: 600 }}>{c.highest_bid || "No bids yet"}</span></p>
              {/* Offer details */}
              {c.discount && c.minQuantity && (
                <div style={{ background: '#ffb300', color: '#fff', borderRadius: 8, padding: '6px 16px', fontWeight: 700, fontSize: 16, margin: '12px 0', display: 'inline-block' }}>
                  {c.discount}% discount for min {c.minQuantity} quantity
                </div>
              )}
            </div>

            <div className="bid-section" style={{ width: '100%', marginTop: 18, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div style={{ display: 'flex', gap: 12, alignItems: 'center', marginBottom: 10 }}>
                <input
                  type="number"
                  placeholder="Quantity"
                  value={bidValues[`${c.id}_qty`] || ""}
                  style={{ border: '1.5px solid #b2dfdb', borderRadius: 8, padding: '10px 16px', fontSize: 16, width: 110, background: '#e0f7fa' }}
                  onChange={e => setBidValues({ ...bidValues, [`${c.id}_qty`]: e.target.value })}
                />
               <input
  type="number"
  placeholder={`${c.base_price - 5} to ${c.base_price + 5}`}
  value={bidValues[c.id] || ""}
  min={c.base_price - 5}
  max={c.base_price + 5}
  step=".1" // use integer steps, or "0.01" if decimals allowed
  style={{
    border: "2px solid #26a69a",
    borderRadius: 8,
    padding: "10px 16px",
    fontSize: 16,
    width: 180,
    background: "#e0f7fa"
  }}
  onChange={e => setBidValues({ ...bidValues, [c.id]: e.target.value })}
/>

              </div>
              <button onClick={() => handleBid(c.id, bidValues[c.id], bidValues[`${c.id}_qty`], c.base_price)}
                style={{ background: '#26c6da', color: '#fff', border: 'none', borderRadius: 10, padding: '12px 32px', fontWeight: 'bold', fontSize: 18, cursor: 'pointer', boxShadow: '0 2px 8px #b2dfdb', marginTop: 6, transition: 'background 0.2s' }}
                onMouseOver={e => e.currentTarget.style.background = '#00897b'}
                onMouseOut={e => e.currentTarget.style.background = '#26c6da'}
              >
                Send Request
              </button>
            </div>
          </div>
        ))}
      </div>

    
    </div>
  );
}
