// frontend/src/pages/FarmerMarketplace.jsx
import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [myCrops, setMyCrops] = useState([]);
  const [otherCrops, setOtherCrops] = useState([]);
  const [marketplaceForm, setMarketplaceForm] = useState({}); // key: cropId -> { price, quantity }

  // Fetch all crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/crops", { credentials: "include" });
      const data = await res.json();
      // Separate own crops and other farmers
      setMyCrops(data.filter(c => c.farmer_id === user.id));
      setOtherCrops(data.filter(c => c.farmer_id !== user.id));
    } catch (err) {
      console.error("Error fetching crops", err);
    }
  };

  useEffect(() => {
    if (user) fetchCrops();
  }, [user]);

  // Handle adding crop to marketplace
  const handleAddToMarketplace = async (crop) => {
    const formData = marketplaceForm[crop.id];
    if (!formData?.price || !formData?.quantity) return alert("Enter price and quantity");

    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/harvest/${crop.id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          price: parseFloat(formData.price),
          quantity: parseInt(formData.quantity)
        })
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message);
      alert("Crop added to marketplace!");
      setMarketplaceForm({ ...marketplaceForm, [crop.id]: { price: "", quantity: "" } });
      fetchCrops();
    } catch (err) {
      console.error(err);
      alert("Error adding crop to marketplace");
    }
  };

  return (
    <div style={{ minHeight: "100vh", padding: "32px 0", background: "#f9fbe7" }}>
      <div style={{ maxWidth: 1200, margin: "0 auto" }}>
        {/* My Crops Section */}
        <h2 style={{ fontSize: 32, fontWeight: 700, color: "#26a69a", marginBottom: 16 }}>My Crops</h2>
        {myCrops.length === 0 && <p>No crops yet.</p>}
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: 24 }}>
          {myCrops.map(crop => (
            <div key={crop.id} style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 2px 6px #b2dfdb" }}>
              <h3 style={{ fontSize: 22, fontWeight: 700, color: "#00897b" }}>{crop.name}</h3>
              <p>Quantity: {crop.quantity} {crop.unit}</p>
              <p>Completion: {crop.completion_percentage || 0}%</p>

              {crop.completion_percentage >= 80 ? (
                <>
                  <input
                    type="number"
                    placeholder="Base price"
                    value={marketplaceForm[crop.id]?.price || ""}
                    onChange={e => setMarketplaceForm({ ...marketplaceForm, [crop.id]: { ...marketplaceForm[crop.id], price: e.target.value } })}
                    style={{ width: "48%", marginRight: "4%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
                  />
                  <input
                    type="number"
                    placeholder="Quantity"
                    value={marketplaceForm[crop.id]?.quantity || ""}
                    onChange={e => setMarketplaceForm({ ...marketplaceForm, [crop.id]: { ...marketplaceForm[crop.id], quantity: e.target.value } })}
                    style={{ width: "48%", padding: 8, borderRadius: 6, border: "1px solid #b2dfdb" }}
                  />
                  <button
  onClick={() => handleAddToMarketplace(crop)}
  disabled={crop.completion_percentage < 80}
  style={{
    marginTop: 10,
    padding: "8px 16px",
    borderRadius: 8,
    background: crop.completion_percentage >= 80 ? "#26c6da" : "#ccc",
    color: "#fff",
    border: "none",
    fontWeight: 600,
    cursor: crop.completion_percentage >= 80 ? "pointer" : "not-allowed"
  }}
>
  Add to Marketplace
</button>
<p style={{ color: "#e57373", marginTop: 10 }}>
  Completion: {crop.completion_percentage || 0}%
</p>
                </>
              ) : (
                <p style={{ color: "#e57373", marginTop: 10 }}>Cannot add to marketplace until 80% completion</p>
              )}
            </div>
          ))}
        </div>

        {/* Other Farmers’ Crops Section */}
        <h2 style={{ fontSize: 32, fontWeight: 700, color: "#26a69a", margin: "40px 0 16px 0" }}>Other Farmers’ Crops</h2>
        {otherCrops.length === 0 && <p>No other crops available.</p>}
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", gap: 24 }}>
          {otherCrops.map(crop => (
            <div key={crop.id} style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 2px 6px #b2dfdb" }}>
              <h3 style={{ fontSize: 22, fontWeight: 700, color: "#00897b" }}>{crop.name}</h3>
              <p>Quantity: {crop.quantity} {crop.unit}</p>
              <p>Base Price: {crop.base_price} Tk</p>
              <p>Farmer: {crop.farmer_name}</p>
              <p style={{ color: "#888", fontStyle: "italic" }}>Read-only, cannot add to marketplace</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
