import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function DealerDetailsPage() {
  const { dealerId, requestId } = useParams();
  const [details, setDetails] = useState(null);
  const [dealDone, setDealDone] = useState(false); // show popup

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/requests/${requestId}`, { credentials: "include" });
        const data = await res.json();
        setDetails(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchDetails();
  }, [dealerId, requestId]);

  const handleAction = async (status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/handle/${requestId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ status }),
      });

      const data = await res.json();

      if (res.ok && status === "accepted") {
        setDealDone(true); // show congratulation popup
      } else if (res.ok && status === "rejected") {
        alert("Request rejected successfully.");
      }

      // Refresh details
      setDetails(prev => ({ ...prev, status }));
    } catch (err) {
      console.error(err);
      alert("Error processing request.");
    }
  };

  if (!details) return <p>Loading...</p>;

  const totalPrice = details.bid_price * details.requested_quantity;

  return (
    <div>
      <h2>Dealer Request Details</h2>
      <p><strong>Dealer Name:</strong> {details.dealer_name}</p>
      <p><strong>Email:</strong> {details.dealer_email}</p>
      <p><strong>Phone:</strong> {details.dealer_phone}</p>
      <p><strong>Address:</strong> {details.dealer_address}</p>

      <h3>Crop Info</h3>
      <p><strong>Crop:</strong> {details.crop_name}</p>
      <p><strong>Quantity:</strong> {details.requested_quantity} {details.unit}</p>
      <p><strong>Bid Price:</strong> {details.bid_price} Tk</p>
      <p><strong>Total Price:</strong> {totalPrice} Tk</p>
      <p><strong>Status:</strong> {details.status || "Pending"}</p>

      {/* Accept / Reject buttons for farmer */}
      {!details.status && (
        <div style={{ marginTop: "20px" }}>
          <button onClick={() => handleAction("accepted")} style={{ marginRight: "10px" }}>Accept</button>
          <button onClick={() => handleAction("rejected")}>Reject</button>
        </div>
      )}

      {/* ✅ Congratulation popup */}
      {dealDone && (
        <div className="popup">
          <h3>🎉 Deal Done!</h3>
          <p>You have accepted the request. The dealer has been notified and a PDF receipt has been generated.</p>
          {details.extra_json?.receipt_url && (
            <a href={details.extra_json.receipt_url} target="_blank" rel="noopener noreferrer">
              Download PDF
            </a>
          )}
          <button onClick={() => setDealDone(false)}>Close</button>
        </div>
      )}
    </div>
  );
}
