import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function DealerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [bids, setBids] = useState({}); // Track dealer's bid input per crop

  // Fetch all crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Place a bid
  const handleBid = async (crop) => {
    const bidPrice = parseFloat(bids[crop.id]);
    if (isNaN(bidPrice)) return alert("Enter a valid bid");

    const minBid = crop.base_price - 5;
    const maxBid = crop.base_price + 5;

    if (bidPrice < minBid || bidPrice > maxBid) {
      return alert(`Bid must be between ${minBid} and ${maxBid} Tk`);
    }

    try {
      await fetch(`http://localhost:5000/api/marketplace/bid`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ crop_id: crop.id, bid_price: bidPrice }),
      });
      fetchCrops(); // refresh data to show updated highest bid
      setBids({ ...bids, [crop.id]: "" }); // clear input
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="dealer-marketplace-container">
      <h2>Dealer Marketplace</h2>

      <div className="crop-cards">
        {crops.length === 0 ? (
          <p>No crops available.</p>
        ) : (
          crops.map(c => (
            <div key={c.id} className="crop-card">
              <h3>{c.name}</h3>
              <p>
                Quantity: {c.quantity} {c.unit}<br/>
                Base Price: {c.base_price} Tk<br/>
                Highest Bid: {c.highest_bid || "No bids yet"} Tk<br/>
                Farmer: {c.farmer_name || "N/A"}
              </p>

              <div className="bid-section">
                <input
                  type="number"
                  placeholder={`Bid (${c.base_price - 5} - ${c.base_price + 5})`}
                  value={bids[c.id] || ""}
                  onChange={e => setBids({ ...bids, [c.id]: e.target.value })}
                />
                <button onClick={() => handleBid(c)}>Place Bid</button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Styles */}
      <style jsx>{`
        .dealer-marketplace-container {
          padding: 20px;
          max-width: 900px;
          margin: auto;
        }
        .crop-cards {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
          gap: 15px;
        }
        .crop-card {
          border: 1px solid #ccc;
          border-radius: 8px;
          padding: 15px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        .bid-section {
          display: flex;
          gap: 5px;
          margin-top: 10px;
        }
        .bid-section input {
          flex: 1;
          padding: 6px;
          border-radius: 5px;
          border: 1px solid #ccc;
        }
        .bid-section button {
          background-color: #007bff;
          color: white;
          border: none;
          padding: 6px 10px;
          cursor: pointer;
          border-radius: 5px;
        }
      `}</style>
    </div>
  );
}
