import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function DealerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [bidValues, setBidValues] = useState({});

  const fetchCrops = async () => {
    const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
    const data = await res.json();
    setCrops(data);
  };

  const handleBid = async (cropId, bidPrice) => {
    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/bid/${cropId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ bid_price: parseFloat(bidPrice) })
      });
      const data = await res.json();
      if (!res.ok) alert(data.message);
      else fetchCrops();
    } catch (err) {
      alert("Error placing bid.");
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="dealer-marketplace-container">
      <h2>ðŸ›’ Dealer Marketplace</h2>

      {crops.length === 0 && <p>No crops available yet.</p>}

      <div className="crop-cards">
        {crops.map(c => (
          <div key={c.id} className="crop-card">
            <div className="crop-info">
              <h3>{c.name}</h3>
              <p>Quantity: {c.quantity} {c.unit}</p>
              <p>Base Price: <strong>{c.base_price} Tk</strong></p>
              <p>Highest Bid: <span className="highlight">{c.highest_bid || "No bids yet"}</span></p>
            </div>

            <div className="bid-section">
              <input
                type="number"
                placeholder={`Bid ${c.base_price - 5} - ${c.base_price + 5}`}
                value={bidValues[c.id] || ""}
                onChange={e => setBidValues({ ...bidValues, [c.id]: e.target.value })}
              />
              <button onClick={() => handleBid(c.id, bidValues[c.id])}>
                Place Bid
              </button>
            </div>
          </div>
        ))}
      </div>
      <div className="request-form">
  <h3>Request a Product</h3>
  <input
    type="text"
    placeholder="Product Name"
    value={request.product}
    onChange={e => setRequest({ ...request, product: e.target.value })}
  />
  <input
    type="number"
    placeholder="Quantity (kg/unit)"
    value={request.quantity}
    onChange={e => setRequest({ ...request, quantity: e.target.value })}
  />
  <input
    type="number"
    placeholder="Your Price per unit"
    value={request.price}
    onChange={e => setRequest({ ...request, price: e.target.value })}
  />
  <button onClick={handleRequest}>Send Request</button>
</div>


      {/* Styles */}
      <style jsx>{`
        .dealer-marketplace-container {
          padding: 20px;
          max-width: 1350px;
          margin: auto;
          font-family: Arial, sans-serif;
        }

        h2 {
          text-align: center;
          margin-bottom: 20px;
          color: #2c3e50;
        }

        .crop-cards {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
          gap: 25px;
        }

        .crop-card {
          background: linear-gradient(135deg, #e0f7fa, #ffffff);
          border: 1px solid #eaeaea;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.08);
          padding: 20px;
          transition: transform 0.2s ease, box-shadow 0.2s ease;
        }

        .crop-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 6px 16px rgba(0,0,0,0.12);
        }

        .crop-info h3 {
          margin: 0 0 10px;
          color: #007bff;
        }

        .crop-info p {
          margin: 6px 0;
          font-size: 0.95rem;
          color: #333;
        }

        .highlight {
          color: #e67e22;
          font-weight: bold;
        }

        .bid-section {
          display: flex;
          gap: 8px;
          margin-top: 15px;
        }

        .bid-section input {
          flex: 1;
          padding: 8px;
          border-radius: 6px;
          border: 1px solid #ccc;
        }

        .bid-section button {
          background: #007bff;
          color: white;
          border: none;
          padding: 3px 1px;
          border-radius: 5px;
          cursor: pointer;
          transition: background 0.2s ease;
        }

        .bid-section button:hover {
          background: #0056b3;
        }
      `}</style>
    </div>
  );
}
