import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { io } from "socket.io-client";
import moment from "moment";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);
  const [socket, setSocket] = useState(null);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(`http://localhost:5000/api/requests/notifications/${user.id}`, { credentials: "include" });
      if (res.ok) {
        const data = await res.json();
        setNotifications(data.notifications || []);
      }
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  };

  const markAsRead = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/requests/notifications/read/${id}`, {
        method: "POST",
        credentials: "include",
      });
      setNotifications((prev) =>
        prev.map((n) => (n.id === id ? { ...n, read_flag: 1 } : n))
      );
    } catch (err) {
      console.error("Failed to mark as read:", err);
    }
  };

  useEffect(() => {
    if (!user) return;

    fetchNotifications();

    const newSocket = io("http://localhost:5000", { withCredentials: true });
    setSocket(newSocket);

    // Join user-specific room
    newSocket.emit("joinRoom", `farmer_${user.id}`);

    // Listen for new notifications
    newSocket.on("newRequest", (notif) => {
      setNotifications((prev) => [notif, ...prev]);
    });

    return () => newSocket.disconnect();
  }, [user]);

  const unreadCount = notifications.filter((n) => !n.read_flag).length;

  return (
    <nav className="navbar flex justify-between items-center p-4 bg-white shadow">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>

      <div className="nav-right flex items-center gap-4">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* Notification Bell */}
            <div className="notification-wrapper relative">
              <button
                className="notification-bell relative text-2xl"
                onClick={() => setOpen(!open)}
              >
                🔔
                {unreadCount > 0 && (
                  <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full animate-pulse"></span>
                )}
              </button>

              {open && (
                <div className="notification-dropdown absolute right-0 mt-2 w-80 bg-white shadow-lg rounded z-50 max-h-96 overflow-y-auto">
                  <h4 className="p-2 font-bold border-b">Notifications</h4>
                  {notifications.length === 0 ? (
                    <p className="p-3 text-sm text-gray-500">No notifications</p>
                  ) : (
                    notifications.map((n) => (
                      <div
                        key={n.id}
                        onClick={() => markAsRead(n.id)}
                        className={`p-3 border-b cursor-pointer rounded ${
                          n.read_flag
                            ? "bg-white text-black"
                            : "bg-blue-50 text-blue-800 font-medium"
                        }`}
                      >
                        <span className="block">
                          You have a new request for{" "}
                          <strong>{n.crop_name}</strong> {n.requested_quantity} {n.unit} at {n.bid_price} Tk
                        </span>
                        <span className="text-xs text-gray-500">
                          {moment(n.created_at).fromNow()}
                          {!n.read_flag && <span className="ml-2 text-red-500">●</span>}
                        </span>
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar w-8 h-8 rounded-full"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
