import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerCropPage() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    name: "",
    land_size: "",
    cultivate_date: "",
    harvest_date: "",
    total_cost: "",
  });

  const fetchCrops = async () => {
    if (!user) return;
    try {
      const res = await fetch(`http://localhost:5000/api/crops/farmer/${user.id}`, { credentials: "include" });
      const data = await res.json();
      setCrops(data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCrops();
  }, [user]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleAddCrop = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/crops`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ ...form, farmer_id: user.id }),
      });
      if (res.ok) {
        setForm({ name: "", land_size: "", cultivate_date: "", harvest_date: "", total_cost: "" });
        fetchCrops();
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpdateCrop = async (cropId, updatedFields) => {
    try {
      await fetch(`http://localhost:5000/api/crops/${cropId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(updatedFields),
      });
      fetchCrops();
    } catch (err) {
      console.error(err);
    }
  };

  const handleHarvest = async (crop) => {
    try {
      const quantity = prompt(`Enter quantity of ${crop.name} to move to marketplace:`);
      if (!quantity) return;

      await fetch(`http://localhost:5000/api/marketplace`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          crop_id: crop.id,
          crop_name: crop.name,
          quantity,
          farmer_id: user.id,
        }),
      });

      // Optionally remove from farmer crops
      fetchCrops();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <p>Loading crops...</p>;

  return (
    <div>
      <form onSubmit={handleAddCrop} className="mb-6 p-4 border rounded">
        <h3 className="font-bold mb-2">Add New Crop</h3>
        <input
          name="name"
          placeholder="Crop Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="land_size"
          placeholder="Land Size (acre)"
          value={form.land_size}
          onChange={handleChange}
          required
        />
        <input
          name="cultivate_date"
          type="date"
          value={form.cultivate_date}
          onChange={handleChange}
          required
        />
        <input
          name="harvest_date"
          type="date"
          value={form.harvest_date}
          onChange={handleChange}
          required
        />
        <input
          name="total_cost"
          type="number"
          placeholder="Total Cost"
          value={form.total_cost}
          onChange={handleChange}
          required
        />
        <button type="submit" className="mt-2 bg-green-500 text-white px-3 py-1 rounded">Add Crop</button>
      </form>

      <h3 className="font-bold mb-2">My Crops</h3>
      {crops.length === 0 ? (
        <p>No crops added yet.</p>
      ) : (
        <div className="grid gap-4">
          {crops.map((crop) => (
            <div key={crop.id} className="p-3 border rounded">
              <p><strong>{crop.name}</strong></p>
              <p>Land: {crop.land_size} acre</p>
              <p>Cultivate: {crop.cultivate_date} | Harvest: {crop.harvest_date}</p>
              <p>Total Cost: {crop.total_cost} Tk</p>
              <button
                onClick={() => {
                  const newCost = prompt("Update total cost:", crop.total_cost);
                  if (newCost) handleUpdateCrop(crop.id, { total_cost: newCost });
                }}
                className="mr-2 bg-yellow-500 text-white px-2 py-1 rounded"
              >
                Update Cost
              </button>
              <button
                onClick={() => handleHarvest(crop)}
                className="bg-blue-500 text-white px-2 py-1 rounded"
              >
                Harvest
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
