import { useContext, useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const nav = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogout = async () => {
    await logout();
    nav("/login");
  };

  // Fetch notifications (dealer requests) for farmer
  const fetchNotifications = async () => {
    if (!user) return;
    try {
      const res = await fetch(`http://localhost:5000/api/requests/notifications/${user.id}`, {
        credentials: "include"
      });
      const data = await res.json();
      setNotifications(data);
    } catch (err) {
      console.error("Error fetching notifications", err);
    }
  };

  // Handle Accept / Reject request
  const handleAction = async (requestId, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/handle/${requestId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ status }),
      });
      if (res.ok) fetchNotifications(); // refresh notifications
    } catch (err) {
      console.error("Error handling request", err);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // fetch every 5s
    return () => clearInterval(interval);
  }, [user]);

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link to="/"><strong>AgroVision</strong></Link>
      </div>
      <div className="nav-right">
        {user ? (
          <>
            <NavLink to="/home">Home</NavLink>
            <NavLink to="/profile">Profile</NavLink>

            {/* 🔔 Notification Bell */}
            <div className="notification-wrapper">
              <button
                className="notification-bell"
                onClick={() => setOpen(!open)}
              >
                🔔
                {notifications.length > 0 && (
                  <span className="badge">{notifications.length}</span>
                )}
              </button>

           {open && (
  <div className="notification-dropdown">
    <h4>Notifications</h4>
    {notifications.length === 0 ? (
      <p>No new notifications</p>
    ) : (
      notifications.map((n) => {
        const isUnread = !n.read_status;
        const createdAt = new Date(n.created_at);
        const timeAgo = isNaN(createdAt) ? "" : createdAt.toLocaleString();

        const handleClick = async () => {
          // Navigate to dealer details page
          nav(`/dealer/${n.dealer_id}/request/${n.request_id}`);

          if (isUnread) {
            try {
              // Mark as read in backend
              await fetch(`http://localhost:5000/api/notifications/read/${n.id}`, {
                method: "POST",
                credentials: "include",
              });

              // Update state locally
              setNotifications((prev) =>
                prev.map((item) =>
                  item.id === n.id ? { ...item, read_status: true } : item
                )
              );
            } catch (err) {
              console.error("Failed to mark notification as read", err);
            }
          }
        };

        return (
          <div
            key={n.id}
            className={`notification-item ${isUnread ? "unread" : "read"}`}
            onClick={handleClick}
          >
            <span className="notification-text">
              You have a new request for <strong>{n.crop_name}</strong>: {n.requested_quantity} {n.unit} at {n.bid_price} Tk
            </span>
            <span className="notification-meta">
              {timeAgo}
              {isUnread && <span className="new-badge">New</span>}
            </span>
          </div>
        );
      })
    )}
  </div>
)}



            </div>

            <button onClick={handleLogout}>Logout</button>
            <img
              src={user.profile_pic || "/default-avatar.png"}
              alt="profile"
              className="nav-avatar"
            />
          </>
        ) : (
          <>
            <NavLink to="/login">Login</NavLink>
            <NavLink to="/register">Register</NavLink>
          </>
        )}
      </div>
    </nav>
  );
}
