import './styles/Marketplace.css'; // example if in src/styles/

import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function DealerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [bidValues, setBidValues] = useState({});

  const fetchCrops = async () => {
    const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
    const data = await res.json();
    setCrops(data);
  };

  const handleBid = async (cropId, bidPrice) => {
    try {
      const res = await fetch(`http://localhost:5000/api/marketplace/bid/${cropId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ bid_price: parseFloat(bidPrice) })
      });
      const data = await res.json();
      if (!res.ok) alert(data.message);
      else fetchCrops();
    } catch (err) {
      alert("Error placing bid.");
    }
  };

  useEffect(() => { fetchCrops(); }, []);

  return (
    <div className="marketplace-container">
      <h2>Dealer Marketplace</h2>
      {crops.length === 0 && <p>No crops available yet.</p>}
      {crops.map(c => (
        <div key={c.id} className="crop-card">
          <strong>{c.name}</strong> - {c.quantity} {c.unit} - Base: {c.base_price} Tk
          <div>Highest Bid: {c.highest_bid || "No bids yet"}</div>
          <div className="bid-section">
            <input
              type="number"
              placeholder={`Bid between ${c.base_price - 5} - ${c.base_price + 5}`}
              value={bidValues[c.id] || ""}
              onChange={e => setBidValues({ ...bidValues, [c.id]: e.target.value })}
            />
            <button onClick={() => handleBid(c.id, bidValues[c.id])}>Place Bid</button>
          </div>
        </div>
      ))}
    </div>
  );
}
