import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function FarmerMarketplace() {
  const { user } = useContext(AuthContext);
  const [crops, setCrops] = useState([]);
  const [requests, setRequests] = useState([]); // Dealer requests state
  const [form, setForm] = useState({
    name: "",
    quantity: "",
    unit: "kg",
    base_price: ""
  });

  // Fetch farmer's crops
  const fetchCrops = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/marketplace", { credentials: 'include' });
      const data = await res.json();
      const myCrops = data.filter(c => c.farmer_id === user.id);
      setCrops(myCrops);
    } catch (err) {
      console.error("Error fetching crops:", err);
    }
  };

  // Fetch dealer requests for this farmer
  const fetchRequests = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/requests/${user.id}`, { credentials: "include" });
      const data = await res.json();
      setRequests(data);
    } catch (err) {
      console.error("Error fetching requests", err);
    }
  };

  const handleAddCrop = async () => {
    try {
      await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form)
      });
      setForm({ name: "", quantity: "", unit: "kg", base_price: "" });
      fetchCrops();
    } catch (err) {
      alert("Error adding crop.");
    }
  };

  useEffect(() => {
    fetchCrops();
    fetchRequests();
    const interval = setInterval(fetchRequests, 5000); // Fetch requests every 5 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="farmer-marketplace-container">
      <h2>🌾 Farmer Marketplace</h2>

      {/* Add Crop Form */}
      <div className="add-crop-form">
        <input
          placeholder="Crop Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          placeholder="Quantity"
          type="number"
          value={form.quantity}
          onChange={e => setForm({ ...form, quantity: e.target.value })}
        />
        <select value={form.unit} onChange={e => setForm({ ...form, unit: e.target.value })}>
          <option value="kg">kg</option>
          <option value="unit">unit</option>
        </select>
        <input
          placeholder="Price per unit/kg"
          type="number"
          value={form.base_price}
          onChange={e => setForm({ ...form, base_price: e.target.value })}
        />
        <button onClick={handleAddCrop}>+ Add Crop</button>
      </div>

      {/* Crops Section */}
      <div className="crops-section">
        <h3>Your Crops</h3>
        {crops.length === 0 && <p>No crops added yet.</p>}

        <div className="crop-cards">
          {crops.map(c => (
            <div key={c.id} className="crop-card">
              <div className="crop-info">
                <h3>{c.name}</h3>
                <p>Quantity: {c.quantity} {c.unit}</p>
                <p>Base Price: {c.base_price} Tk</p>
                <p>Highest Bid: {c.highest_bid || "No bids yet"}</p>
              </div>

              {c.bids && c.bids.length > 0 && (
                <div className="bids-list">
                  <h4>Dealer Bids</h4>
                  <ul>
                    {c.bids.map(b => (
                      <li key={b.id}>
                        <span>{b.dealer_name}</span>
                        <strong>{b.bid_price} Tk</strong>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Dealer Requests Section */}
      <div className="notification-panel">
        <h3>🔔 Dealer Requests</h3>
        {requests.length === 0 ? (
          <p>No new requests</p>
        ) : (
          requests.map(r => (
            <div key={r.id} className="notification-card">
              <h4>{r.dealer_name}</h4>
              <p><b>Phone:</b> {r.dealer_phone}</p>
              <p><b>Address:</b> {r.dealer_address}</p>
              <p><b>Crop:</b> {r.crop_name}</p>
              <p><b>Requested:</b> {r.requested_quantity} {r.unit}</p>
              <p><b>Offered Price:</b> {r.bid_price} Tk</p>
              <div className="action-buttons">
                <button className="accept">Accept</button>
                <button className="reject">Reject</button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Styles */}
      <style jsx>{`
        .farmer-marketplace-container { padding: 20px; max-width: 1300px; margin: auto; font-family: Arial, sans-serif; }
        h2 { text-align: center; margin-bottom: 20px; color: #2c3e50; }
        .add-crop-form { display: flex; gap: 10px; flex-wrap: wrap; margin-bottom: 30px; background: #f9fafc; padding: 15px; border-radius: 10px; box-shadow: 0 3px 6px rgba(0,0,0,0.08); }
        .add-crop-form input, .add-crop-form select { padding: 10px; border: 1px solid #ddd; border-radius: 6px; flex: 1; min-width: 150px; }
        .add-crop-form button { background: #28a745; color: white; border: none; padding: 10px 15px; border-radius: 6px; cursor: pointer; transition: background 0.2s ease; }
        .add-crop-form button:hover { background: #218838; }
        .crops-section { margin-bottom: 30px; }
        .crop-cards { display: grid; grid-template-columns: repeat(auto-fill, minmax(260px, 1fr)); gap: 20px; }
        .crop-card { background: #fff; border: 1px solid #eaeaea; border-radius: 12px; box-shadow: 0 4px 10px rgba(0,0,0,0.05); padding: 15px; transition: transform 0.2s ease, box-shadow 0.2s ease; }
        .crop-card:hover { transform: translateY(-5px); box-shadow: 0 6px 16px rgba(0,0,0,0.1); }
        .crop-info h3 { margin: 0 0 8px; color: #007bff; }
        .bids-list { margin-top: 15px; padding-top: 10px; border-top: 1px solid #eee; }
        .bids-list ul { list-style: none; padding: 0; margin: 0; }
        .bids-list li { display: flex; justify-content: space-between; padding: 5px 0; font-size: 0.9rem; border-bottom: 1px dashed #eee; }
        .bids-list li:last-child { border-bottom: none; }
        .bids-list strong { color: #28a745; }
        .notification-panel { background: #fdfdfd; padding: 15px; border-radius: 12px; border: 1px solid #eaeaea; box-shadow: 0 4px 10px rgba(0,0,0,0.05); max-height: 600px; overflow-y: auto; }
        .notification-card { background: #fff; padding: 12px; border-radius: 8px; border: 1px solid #ddd; margin-bottom: 12px; }
        .notification-card h4 { margin: 0 0 6px; color: #007bff; }
        .action-buttons { display: flex; gap: 10px; margin-top: 10px; }
        .accept { background: #28a745; color: white; border: none; padding: 6px 12px; border-radius: 6px; cursor: pointer; }
        .reject { background: #dc3545; color: white; border: none; padding: 6px 12px; border-radius: 6px; cursor: pointer; }
      `}</style>
    </div>
  );
}
