import { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import  AuthContext  from '../context/AuthContext';
import FarmerCropPage from './FarmerCropPage';

export default function FarmerHomePage() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  if (!user || user.role !== "farmer") {
    return <p>Access denied. You are not a farmer.</p>;
  }

  return (
    <div>
      {/* Hero */}
      <header className="hero">
        <div className="hero-overlay">
          <h1>Welcome to <span className="highlight">AgroVision</span></h1>
          <p>Empowering farmers & dealers with technology.</p>
        </div>
      </header>

      <main className="container">
        {/* Services */}
        <section className="cards">
          <div className="card">
            <h3>Marketplace</h3>
            <p>Farmers can sell crops.</p>
            <button onClick={() => navigate("/farmer-marketplace")}>Go to Marketplace</button>
          </div>

          <div className="card">
            <h3>Knowledge</h3>
            <p>Access crop guides & tips.</p>
            <button onClick={() => navigate("/knowledge")}>Open Guides</button>
          </div>
        </section>

        {/* My Crops */}
        <section className="my-crops mt-8">
          <h2>My Crops</h2>
          <FarmerCropPage />
        </section>
      </main>

      <footer className="site-footer">
        <div>© {new Date().getFullYear()} AgroVision — Contact: +880 xxxxxxxx</div>
      </footer>
    </div>
  );
}
